-- ----------------------------
-- Table structure for news_article
-- ----------------------------
DROP TABLE IF EXISTS `news_article`;
CREATE TABLE `news_article` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '文章逻辑ID',
  `title` varchar(128) NOT NULL COMMENT '文章标题',
  `cate_id` int(11) NOT NULL DEFAULT '1' COMMENT '文章类别',
  `photo` varchar(64) DEFAULT '' COMMENT '文章图片',
  `remark` varchar(256) DEFAULT '' COMMENT '文章描述',
  `keyword` varchar(32) DEFAULT '' COMMENT '文章关键字',
  `content` text NOT NULL COMMENT '文章内容',
  `views` int(11) NOT NULL DEFAULT '1' COMMENT '浏览量',
  `type` int(1) NOT NULL DEFAULT '1' COMMENT '文章类型',
  `is_tui` int(1) DEFAULT '0' COMMENT '是否推荐',
  `from` varchar(16) NOT NULL DEFAULT '' COMMENT '来源',
  `writer` varchar(64) NOT NULL COMMENT '作者',
  `ip` varchar(16) NOT NULL COMMENT 'IP',
  `create_time` int(11) NOT NULL COMMENT '创建时间',
  `update_time` int(11) DEFAULT NULL COMMENT '更新时间',
  `status` enum('3','2','1','0') NOT NULL DEFAULT '1' COMMENT '0 删除 1 待审核 2 发布 3 未通过',
  `back` varchar(128) DEFAULT '' COMMENT '未通过原因',
  `sort` varchar(10) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `a_title` (`title`)
) ENGINE=MyISAM AUTO_INCREMENT=0 DEFAULT CHARSET=utf8 COMMENT='文章表';

-- ----------------------------
-- Records of news_article
-- ----------------------------
INSERT INTO `news_article` VALUES ('1', '入伏之后，切记“七不做”，让你度过健康一整年', '2', '20170721\\87e5605778a4e2bc77ef3d15527bb11d.jpg', '入伏，也就意味着一年中最炎热的盛夏高温季节就此开始。夏秋之交闷热潮湿且多雨，更湿气盛行之时。三伏天不止是养生的好时节，更加是一个需要避病的日子。', '', '<p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">入伏，也就意味着一年中最炎热的盛夏高温季节就此开始。夏秋之交闷热潮湿且多雨，更湿气盛行之时。三伏天不止是养生的好时节，更加是一个需要避病的日子。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ee600014c3cd656b693\" img_width=\"500\" img_height=\"333\" alt=\"入伏之后，切记“七不做”，让你度过健康一整年\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">夏季是很多人最不喜欢的季节，因为不仅热，而且湿气大。但是在中医的眼中，对于人们的健康来说，这是个“冬病夏治”好机会。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">每年三伏酷暑的时候，大家要额外注意，七件事情千万不能做：</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">1</span><span style=\"font-weight: 700;\">、</span><span style=\"font-weight: 700;\">不</span><span style=\"font-weight: 700;\">久坐不动</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">夏天动一动就出汗，久坐会使人供血不足，加重乏力和肌肉酸痛。夏天不可以完全不动，应该要稍微活动一下。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2ee20002cbbd27ded345\" img_width=\"517\" img_height=\"327\" alt=\"入伏之后，切记“七不做”，让你度过健康一整年\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">2</span><span style=\"font-weight: 700;\">、不对着空调</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">一身汗或者运动后大汗淋漓，对着空调使劲吹凉风，湿气会借机顺着张开的汗毛孔进入人体。现在冬天很少见到着凉感冒的人，夏天反而比比皆是，就是因为用空调导致汗液挥发不出来淤积体内。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p9.pstatp.com/large/2ee6000151389f11c408\" img_width=\"366\" img_height=\"220\" alt=\"入伏之后，切记“七不做”，让你度过健康一整年\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">3</span><span style=\"font-weight: 700;\">、熬夜是大忌</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">很多人喜欢熬夜，从中医角度来说，睡眠不足就是脾虚的症状，表明体内湿气过重。每天晚上最好11点前入睡，每天睡够7个小时。适当午睡也是可以的，不过最好别睡过头了，午觉保持在一个小时之内。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2edb0002d4d2555baac4\" img_width=\"500\" img_height=\"323\" alt=\"入伏之后，切记“七不做”，让你度过健康一整年\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">4</span><span style=\"font-weight: 700;\">、</span><span style=\"font-weight: 700;\">要</span><span style=\"font-weight: 700;\">吹干头发</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">夏天时，有的人洗完澡头发湿漉漉的，就坐在空调屋里，甚至头发不吹干就睡觉，导致湿气入侵身体。所以，夏天也要把头发吹干再入睡。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2ed80002cfa6ea2b73bc\" img_width=\"960\" img_height=\"640\" alt=\"入伏之后，切记“七不做”，让你度过健康一整年\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">5</span><span style=\"font-weight: 700;\">、西瓜解暑不宜多吃</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">预防中暑，是大家首先想到的问题。夏季瓜果众多，特别是西瓜，的确能解暑，但不可多食。因其性寒，日常可配些红糖姜茶以平衡其寒性。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ed80002d264a74f888c\" img_width=\"550\" img_height=\"366\" alt=\"入伏之后，切记“七不做”，让你度过健康一整年\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">6、</span><span style=\"font-weight: 700;\">不吃冰饮、雪糕</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">如果怕热，可以喝常温的白开水。如果你能坚持一个三伏天不碰冰饮、雪糕，即使不“冬病夏治”，体内的顽固寒气也能自己好大半。尤其是一到冬天就特别怕冷、手脚冰凉的。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2ee100038675c9c504b9\" img_width=\"400\" img_height=\"297\" alt=\"入伏之后，切记“七不做”，让你度过健康一整年\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">夏天不宜吃生冷的，但是冷粥来上一碗还是可以的。这个冷粥，不是说加了沙冰的那种，而是经过冷藏之后，恢复到室温稍带凉意的粥。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">夏天吃点绿豆也是不错的，有起到解暑的作用。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">绿豆百合薏米粥：</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">材料：绿豆50g 鲜百合30g 薏米100g 冰糖</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">做法：1、将绿豆、薏米洗净，浸泡分2个小时；将百合洗净备用</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2、在开水锅中放入绿豆和薏米，烧开8分钟后改用小火煮至食物开花；</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">3、再加入百合，煮成粥后放入适当的冰糖即可。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ed80002d8819eac36cf\" img_width=\"550\" img_height=\"350\" alt=\"入伏之后，切记“七不做”，让你度过健康一整年\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">7、夏练三伏，但别傻练</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">常说的“夏练三伏”，并不是让大家不分时间、地点、情况地去锻炼。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">医学研究表明，夏天当气温超过35℃、日照超过12小时、湿度高于80％的时候，人体会出汗；且情绪调节中枢就会受到明显影响，人也容易心烦意乱。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">这个时候练一练的方法很简单：端坐、闭目、吞津，就是一个好方法。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">方法：两臂自然下垂，放在腿上，双目和口微闭，调匀呼吸；待口中津液较多时，将其吞咽，可连续吞咽3次；然后，上下牙叩动10～15次，可起到养心安神、固齿健脾的效果。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2ee6000156c60f52586b\" img_width=\"360\" img_height=\"250\" alt=\"入伏之后，切记“七不做”，让你度过健康一整年\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">要做的</span><span style=\"font-weight: 700;\">三件</span><span style=\"font-weight: 700;\">事</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">热水洗澡</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">很多人在进入伏天后，会感到四肢乏力、浑身酸困、汗出过多而黏腻不爽。对付这些的一个好办法，就是洗热水澡。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">进入伏天后，大部分人就会把洗热水澡这个习惯给排除了，觉得夏天要洗冷水才够清凉。其实天气越热，湿邪越重，热水洗澡就显得更有必要，而且洗完以后身体会觉得神清气爽，并不会觉得出汗难受。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ed80002d505512e3bcf\" img_width=\"400\" img_height=\"300\" alt=\"入伏之后，切记“七不做”，让你度过健康一整年\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">吃</span><span style=\"font-weight: 700;\">薏米</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">薏米又名薏苡仁，是常用的中药。又是普遍、常吃的食物，味甘淡微寒,有利水消肿、健脾去湿、舒筋除痹、清热排脓等功效，为常用的利水渗湿药。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">薏米红豆粥：</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">材料：薏米10克、红豆20克</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">做法：将薏米、红豆等洗净，红豆、薏米要提前浸泡</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">将所有材料放入适量的水，文火煲3小时，根据个人口味加入冰糖，即可食用。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">为了更好的效果，可以根据自己的身体情况，添加其它的材料。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ee6000157f7e98f2c39\" img_width=\"540\" img_height=\"346\" alt=\"入伏之后，切记“七不做”，让你度过健康一整年\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">空调</span><span style=\"font-weight: 700;\">温度</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">空调致病的最主要的原因之一，是室内外温差过大。建议23℃~26℃的室内温度比较合适。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p9.pstatp.com/large/2edb0002db67d831abc2\" img_width=\"590\" img_height=\"406\" alt=\"入伏之后，切记“七不做”，让你度过健康一整年\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">总结：这七件事你知道了吗？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">空调的温度你调高了吗？</p><p><br/></p>', '1', '1', '1', '健康网', '凌寒', '', '1500618459', '1500618459', '2', '', '');
INSERT INTO `news_article` VALUES ('2', '蛋白、潜血患者如何避免10~30年不发展成尿毒症？', '2', '20170721\\75961761194cff2942465437a38cca57.jpg', '肾衰竭终末期——尿毒症', '蛋白，潜血患者，尿毒症', '<p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">患者：张xx，男，浙江杭州人，32岁，军人</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">诊断：肾衰竭终末期——尿毒症</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">病史：3岁时感冒发烧，到医院检查蛋白2+、潜血1+，当时诊断为慢性肾炎，患者父母没有意识到肾病的严重性，经治疗后顽固性水肿消除，难治性蛋白尿转阴。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2ecd00024d8f35d852dc\" img_width=\"349\" img_height=\"240\" alt=\"蛋白、潜血患者如何避免10~30年不发展成尿毒症？\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">患者心中有“军人梦”，于是当兵、考军校，在多次严格体检中都未曾发现异常。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">不久之前，参加集训，因劳累过度有头晕、恶心，以及呕吐的症状，晕倒在地，经诊断是尿毒症，所有人非常震惊！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">是什么原因，导致30年之后，这位患者患上了尿毒症呢？</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">这是一名典型的初期只是单纯的蛋白尿、潜血最终发展成尿毒症的患者，作为一名军人，运动量、体力消耗等都比普通人要大一些。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ecf00024b1189ea7e73\" img_width=\"298\" img_height=\"240\" alt=\"蛋白、潜血患者如何避免10~30年不发展成尿毒症？\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">在这种情况下，30年的时间里，患者没有感觉到任何的不适，以至于家属知道检查结果后无论如何也不相信，30年前单纯的蛋白潜血已经恶化成了尿毒症！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">这个例子，也为我们<span style=\"font-weight: 700;\">敲响了警钟</span>：蛋白尿、潜血发展到肾功能不全只是一个时间长短的问题（一般潜伏在10~30年），如果不经过有效的治疗，结果就会发展成尿毒症。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">那么，蛋白尿、潜血患者如何避免发展到10~30年后的尿毒症呢？</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2ecf00024b636d5eed15\" img_width=\"384\" img_height=\"236\" alt=\"蛋白、潜血患者如何避免10~30年不发展成尿毒症？\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">一、&nbsp;<span style=\"font-weight: 700;\">血压、血糖有点高，可别不当回事！</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">肾病患者做好血压监测，是一件及其重要的事情。千万不要因为没症状，就觉得自己血压不高，到了医院一测，血压高的能把自己吓一跳！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">还有的肾病患者血压高还不吃降压药？哎呦喂，看您“作”的！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">降压药不仅降血压，还能有效降低尿蛋白，保护我们的肾功能呢！你确定你不吃？！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">只要遵从医嘱，降压药在人体不会有什么太大的副作用。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">如果是糖尿病患者的不控制好血糖。5—10年可能会出现各种并发症，其中肾衰竭、尿毒症是发病较多较为严重的并发症。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ed6000021febd28519f\" img_width=\"456\" img_height=\"240\" alt=\"蛋白、潜血患者如何避免10~30年不发展成尿毒症？\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">二、&nbsp;<span style=\"font-weight: 700;\">尿液有点异常，要警惕！</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">肾脏很沉默，早期可能一点感觉也没有。尿常规检查就成了，能及时发现肾脏异常的重要手段。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">体检的时候，已经尿蛋白、潜血加号，但却因为不痛不痒，不去肾内科就诊，错过了最佳治疗时机。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">等出现尿少、乏力、恶心这些症状时，采取就诊治疗，或许已经肾衰竭、尿毒症了。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ed2000258a01a89ecf6\" img_width=\"386\" img_height=\"240\" alt=\"蛋白、潜血患者如何避免10~30年不发展成尿毒症？\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">三、&nbsp;<span style=\"font-weight: 700;\">切不可听信“谣言”，乱用偏方！</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">不管中医还是西医，对于慢性肾病来讲，治疗都是一个长期的过程，目的都是为了保护肾功能，维持长期稳定。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">很多肾病患者盲目听信偏方，自以为能治愈能除根，可事实上不但治不了病，还因为某些药物的肾毒性，（比较常见的有：雷公藤、关木通、牵牛子、苍耳子、罂粟壳、草乌、使君子等。其中以雷公藤引起的肾损害最多，其次有关木通及一些中成药制剂），引起了肾脏不可逆转的损害。</p><p><br/></p>', '1', '1', '0', '健康网', '凌寒', '', '1500618533', '1500618533', '2', '', '');
INSERT INTO `news_article` VALUES ('3', '女人多久一次性生活才正常？这个数跟你想象的不一样！', '1', '20170721\\4ad2d73763efa7ee75ff6306516756e4.jpg', '对于女人来说，漂亮是她最强烈的追求。  但女人的卵巢分泌的激素就是女人漂亮的源泉。而规律的合适的性生活则可以更好的养护好女人的卵巢，所以，合适的性生活能让女人更加的漂亮。', '女性，性生活', '<p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">对于女人来说，漂亮是她最强烈的追求。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">但女人的卵巢分泌的激素就是女人漂亮的源泉。而规律的合适的性生活则可以更好的养护好女人的卵巢，所以，合适的性生活能让女人更加的漂亮。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">并且女人卵巢分泌的雌激素，也能让女人的私处更加的水润，这也会增加她的欲望，而且水润的私处对于性生活更加的有好处，体验度更好。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ee10003e8d53356cfd8\" img_width=\"608\" img_height=\"396\" alt=\"女人多久一次性生活才正常？这个数跟你想象的不一样！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">若女人性生活太少，卵巢分泌激素就会变少，时间久了，甚至卵巢可能失去分泌这种激素的能力，还有可能出现早衰现象。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">所以，适当的性生活是很有必要的。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">当然了，若过度的话，也不好，若性生活过度，就会使得盆腔经常处于充血的状态，引起下腹部不舒服，还让得阴道常处于开放的状态，容易引起病菌入侵，引发炎症的产生。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">那么，女人一般多久一次才会更好呢？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">其实，正常的，一般一个周有性生活2次以上的，身体都会更加的健康。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">对于体质比较好的，一周3次以上的，这种女人会显得更加的年轻，拥有更好的气色。</p><p><br/></p>', '1', '1', '1', '健康网', '凌寒', '', '1500618706', '1500618706', '2', '', '');
INSERT INTO `news_article` VALUES ('4', '看舌知健康，老中医教你六个私藏绝学，用了百病全无！', '1', '20170721\\ec855f540e9edb5434a0c2f73f7af077.jpg', '舌头是人体健康最直接的反应之一，老中医想知道你有什么病，看一看就知道了。但对于自己来说舌头出现什么情况代表着哪些健康的信息，我们却一无所知。那么通过舌头我们可以了解到什么信息呢？下面就由我来介绍看舌头知健康的方法吧！', '舌，健康，中医', '<p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">舌头是人体健康最直接的反应之一，老中医想知道你有什么病，看一看就知道了。但对于自己来说舌头出现什么情况代表着哪些健康的信息，我们却一无所知。那么通过舌头我们可以了解到什么信息呢？下面就由我来介绍看舌头知健康的方法吧！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2ed70003a70761c8eef8\" img_width=\"383\" img_height=\"409\" alt=\"看舌知健康，老中医教你六个私藏绝学，用了百病全无！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">【看舌头形状】</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">一、舌象淤血</span><br/><img src=\"http://p3.pstatp.com/large/2ee00000b294b6994d66\" img_width=\"470\" img_height=\"281\" alt=\"看舌知健康，老中医教你六个私藏绝学，用了百病全无！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">1、身体表现：善忘、喉咙发干、性情急躁、皮肤干躁、身体疼痛、面色晦暗、口唇发黑、眼圈暗黑、痛经。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2、原因：血液流动不畅、外伤造成、生气(肝郁气滞就容易造成血瘀)、气虚、寒冷造成。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">3、解决方案：三七与西洋参各0.5克，打粉用温水冲服。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">二、舌象血虚</span><br/><img src=\"http://p1.pstatp.com/large/2ed5000529cca9f35da2\" img_width=\"453\" img_height=\"288\" alt=\"看舌知健康，老中医教你六个私藏绝学，用了百病全无！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">1、身体表现：记忆力差、思考力差、睡眠不好、容易失眠、容易疲惫、怕风怕冷、天热手脚温、天冷手脚冰凉。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2、原因：失血、脾胃功能差不能吸收营养、思虑过多消耗心脾之血。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">3、解决方案：西洋参30克打粉，龙眼肉300克，搅拌均匀隔水蒸4小时，每天一勺用开水冲服。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">三、舌象阴虚</span><br/><img src=\"http://p1.pstatp.com/large/2ed90000cf18ff8a0787\" img_width=\"328\" img_height=\"290\" alt=\"看舌知健康，老中医教你六个私藏绝学，用了百病全无！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">1、身体表现：眼干、口躁咽干、手足心发热、大便干躁、眩晕耳鸣、睡眠差、脉搏快、喜冷饮、心烦爱发火、腰膝酸软。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2、原因：津液不足、熬夜、吃辛辣及吃过多热性的食物、生大病。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">3、解决方案：生地、沙参、麦冬、枸杞各9克，石斛6克、当归3克、猪龙骨一节，煲汤服用，一周即可。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">四、舌象阳虚</span><br/><img src=\"http://p1.pstatp.com/large/2ee00000b2febe17be29\" img_width=\"271\" img_height=\"285\" alt=\"看舌知健康，老中医教你六个私藏绝学，用了百病全无！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">1、身体表现：怕冷、喜饮热饮、精神不振、睡眠偏多、口唇色淡、毛发易落、易出汗、大便稀薄、小便清长。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2、原因：过多吃寒凉的药、喝冷饮、体质弱、过份暴露在寒冷的环境里。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">3、解决方案：当归20克、生姜30克，羊肉500克，煲汤服用。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">五、舌象肝气不疏</span><br/><img src=\"http://p1.pstatp.com/large/2ed70003a7eac4a0af1e\" img_width=\"280\" img_height=\"267\" alt=\"看舌知健康，老中医教你六个私藏绝学，用了百病全无！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">1、身体表现：神情抑郁、口苦口干、眩晕、胃口不佳、忽冷忽热、心烦火大、呕逆、胸闷肋疼、失眠多梦。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2、原因：思虑重、压力大(七情内伤：喜则气散、怒则气上、优则气聚、思则气结、悲则气消、恐则气下、惊则气乱)。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">3、解决方案：灯芯草、竹叶各3克，泡水喝，3-5天即可，主要用于突然上火需要疏肝理气。长期肝气不疏主要需要调整“喜怒优思悲恐惊”七情。慈悲心是克制七情最有力的法宝，即仁者寿。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">六、<span style=\"font-weight: 700;\">舌象水湿重</span><br/><img src=\"http://p3.pstatp.com/large/2ee00000b368fa3e6992\" img_width=\"375\" img_height=\"290\" alt=\"看舌知健康，老中医教你六个私藏绝学，用了百病全无！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">1、身体表现：面部(眼皮)浮肿、油光、易生痤疮、腹泻、身重困倦、大便燥结、急躁易怒。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2、原因：气虚、运动少、饮冷饮，造成水湿留在体内。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">3、解决方案：红豆、薏仁各10克，煮水30分钟，代茶饮，祛署湿，适合夏天。冬天寒湿则适应用绿色的花椒，煮水泡脚。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">【看舌苔颜色】</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">一、</span><span style=\"font-weight: 700;\">健康的舌苔</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">呈现淡粉颜色，表面清爽干，不肥厚。口腔内清洁无异味。舌苔本身光润，在其上表面上，没有牙齿咬合的压印。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">二、</span><span style=\"font-weight: 700;\">“上火”的舌苔</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">舌苔上表面的颜色发白。这层白是薄薄的一层，像是长在舌头上面一样。偶尔出现这种情况到没什么关系，如果连续几天，则是身体“上火”了，多喝开水，有几天就可自行消除。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">三、</span><span style=\"font-weight: 700;\">肠胃出问题的舌苔</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">舌苔上表面厚重，上面像糊了一层东西，且颜色发白。此时，如果用干净的手指甲去刮舌苔表面，能刮出这层类似残留物的东西。此现象表征体内的肠胃，尤其是胃部不舒服。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">四、</span><span style=\"font-weight: 700;\">胃寒的舌苔</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">舌苔上表面浑浊。一般原因涉及：胃寒、思想情绪过度紧张、睡眠不好等导致。常伴有口臭、口苦的症状。用指甲刮东西，效果同上。与人面对面交谈时候，得借助口香糖的掩饰。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">五、</span><span style=\"font-weight: 700;\">问题大的舌苔</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">舌苔上表面发黑。这种颜色对应的身体大多是慢性疾病，如果发现有发黑的颜色了，尽快去做个全面检查。因为其对应的是慢性疾病，多半是肾脏器官出现问题，要重视啊。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">总结：你舌像是那种？你的舌苔是什么颜色？</p><p><br/></p>', '1', '1', '0', '健康网', '凌寒', '', '1500618838', '1500618838', '2', '', '');
INSERT INTO `news_article` VALUES ('5', '狂犬病发作是一种什么感觉？', '1', '20170721\\2cd9d4b7938f91d5071338a826863a92.jpg', '狂犬病是因为狂犬病毒导致的一种急性的传染病可以在人或者是动物身上都能发病，狂犬病最容易在狗狼猫等肉食动物身上存在，人得狂犬病多半是因为被动物咬伤感染而导致的。当人得了狂犬病之后非常容易怕水怕风，咽喉会出现痉挛的现象，甚至可以导致患者瘫痪。由于狂犬病具有很强的恐水特征，所以，又可以把狂犬病叫做恐水症。当人得了狂犬病之后中枢神经系统会受到很大的危害', '狂犬病，感觉', '<h1 style=\"margin: 0.5em 0px; padding: 0px 0px 0px 12px; font-size: 18px; line-height: 24px; position: relative;\">狂犬病发作可能会有各种妄想</h1><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"font-weight: 700;\">狂犬病是因为狂犬病毒导致的一种急性的传染病可以在人或者是动物身上都能发病，狂犬病最容易在狗狼猫等肉食动物身上存在，人得狂犬病多半是因为被动物咬伤感染而导致的。当人得了狂犬病之后非常容易怕水怕风，咽喉会出现痉挛的现象，甚至可以导致患者瘫痪。由于狂犬病具有很强的恐水特征，所以，又可以把狂犬病叫做恐水症。当人得了狂犬病之后中枢神经系统会受到很大的危害。</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">1狂犬病这种疾病具有潜伏期，一般潜伏期为一到三个月，当患者处于潜伏期的时候，不会发生症状，但是有的狂犬病潜伏期会非常短，一般七天左右，最长的狂犬病潜伏期可以达到一年以上。狂犬病过了潜伏期之后就开始发病。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><img src=\"http://p1.pstatp.com/large/2c6800113af05ecf4536\" img_width=\"386\" img_height=\"300\" style=\"border: 0px; display: block; max-width: 100%; margin-bottom: 10px; margin-top: 10px;\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">2当狂犬病开始发作的时候患者会出现全身不舒服的感觉，还会高烧不退，身体会觉得非常疲倦，全身都很乏力，心里有不安的感觉还会出现恶心等症状，狂犬病患者对于疼痛声音光线等等外界的刺激往往都具有很强的过敏反应。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">3当狂犬病发作之后患者的咽喉会出现一些症状，比如说，患者的咽喉会出现紧缩感，这可能是因为狂犬病的病毒在患者的伤口附近大量繁殖然后刺激了患者的神经系统而引起的。当狂犬病病毒刺激患者的神经系统之后患者会出现麻木发痒刺痛的感觉。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"font-weight: 700;\">注意事项：</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">当狂犬病的症状变得强烈之后，就会非常明显，患者的身体会出现严重的痉挛，这个时候，患者就会开始精神错乱，会出现一些幻觉，比如说患者可能出现一些妄想的现象，还可能出现幻听怕光怕声音的症状。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">　</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><br/></p><p><a href=\"http://www.wukong.com/answer/6444859317500772622/?showComment=6444859317500772622\" target=\"_blank\" class=\"show-comment\" style=\"cursor: pointer; text-decoration-line: none; color: rgb(153, 153, 153); float: left; line-height: 16px; margin-left: 0px;\"><span class=\"y-icon icon-comments_details\" style=\"position: relative; display: inline-block; line-height: 1; font-size: 12px; color: rgb(202, 202, 202); -webkit-font-smoothing: antialiased; font-family: yFont !important;\"></span>&nbsp;16评论</a></p><p><a href=\"http://www.wukong.com/user/?uid=6209942958\" class=\"answer-user-avatar\" target=\"_blank\" style=\"cursor: pointer; text-decoration-line: none; color: rgb(153, 153, 153); width: 40px; height: 40px; border-radius: 50%; display: inline-block; position: relative; overflow: hidden; margin-right: 8px; float: left;\"><img class=\"\" src=\"http://p3.pstatp.com/thumb/289c00176c1193cac97e\" alt=\"\" style=\"border: 0px; width: 40px; height: 40px;\"/></a><a class=\"answer-user-name\" href=\"http://www.wukong.com/user/?uid=6209942958\" target=\"_blank\" style=\"cursor: pointer; text-decoration-line: none; color: rgb(34, 34, 34); display: block; font-size: 14px; line-height: 20px; margin-top: 2px; white-space: nowrap; width: 490px; text-overflow: ellipsis; overflow: hidden; margin-bottom: 2px;\">谢文文82375033</a><a class=\"answer-user-tag\" style=\"cursor: pointer; color: rgb(153, 153, 153); display: block; margin-top: 2px; width: 620px; margin-bottom: 2px;\">5小时前</a></p><p><span class=\"y-icon icon-digg\" style=\"position: relative; display: inline-block; line-height: 1; color: rgb(202, 202, 202); -webkit-font-smoothing: antialiased; margin-right: 4px; font-family: yFont !important;\"></span><span data-node=\"like-count\">17</span>赞</p><p><span class=\"y-icon icon-thumbsdown_normal\" style=\"position: relative; display: inline-block; line-height: 1; color: rgb(202, 202, 202); -webkit-font-smoothing: antialiased; margin-right: 4px; font-family: yFont !important;\"></span>踩</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"line-height: 28px;\"><img src=\"http://p3.pstatp.com/large/2c610011bb69ca9704b5\" img_width=\"622\" img_height=\"345\" style=\"border: 0px; display: block; max-width: 100%; margin-bottom: 10px;\"/><br/><br/></span></p><p><span style=\"line-height: 28px;\">人被致病或带毒的动物咬伤抓伤后，狂犬病毒侵入破损的伤口，经一断潜伏期后沿着神经纤维逆行到中枢神经而发病。 潜伏期：短则三天，长者二十几年，常见的是10—100天。</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"line-height: 28px;\">这与个体抵抗力、咬伤程度、部位及动物带毒量有关。一般说来，咬得越重，咬伤部位越靠近头部或咬伤部位神经越丰富，则发病越快。 临床上病程分为三期，即前驱期、兴奋期和瘫痪期，但总病程不超过一周。而且目前尚无特效药治疗，也就是说，一旦发病死亡率100%。</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"line-height: 28px;\">前驱期：头痛、低热、食欲不振、倦怠、烦躁、恐惧不安，对声、光、风等刺激敏感并有窒息感。已愈合的伤口周围感觉异常，如发痒、刺痛、麻木、蚁行感。 兴奋期：极度恐惧，怕水、怕风、怕声、怕光。尤其是极端的恐水，甚至听到水的声音都会全身抽搐，所以狂犬病也叫恐水症。这是因为病人交感神经亢奋，喉头肌肉已痉挛，听到水声条件反射喝水而诱发全身神经兴奋、肌肉紧张。</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"line-height: 28px;\"><img src=\"http://p3.pstatp.com/large/2c6e0011b9b5bb4134ac\" img_width=\"500\" img_height=\"300\" style=\"border: 0px; display: block; max-width: 100%; margin-bottom: 10px;\"/><br/><br/></span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"line-height: 28px;\">本期还表现心率加快，大汗淋漓，唾液大量分泌又不能咽下而从嘴角流出，喉舌肌肉痉挛痛苦发出呻吟声，有时象狗的呻叫声，再加之手指疼痛痉挛乱拍乱抓，所以传说狂犬病人象狗一样。 瘫痪期：体力消耗怠尽，痉挛停止，全身瘫软。最终因呼吸肌麻痹，循环衰竭而死亡。</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"line-height: 28px;\">狂犬病是一种可怕的疾病,据媒体报道,最近一名8岁女孩狂犬病发作身亡。下面一起去了解一下狂犬病。 一个8岁的女孩,在半年前被狗咬伤因为没有及时治疗打狂犬疫苗而死亡,让很多人为之惋惜。现在很多的家庭都在养狗,这种宠物可以给人们的生活增添乐趣,但是,城市的狗都会经过防疫部门的检疫,而一些比较落后的乡镇是很难做到全面检疫的,所以,很多的狗并不健康,而且,狗有一种最凶险的疾病就是狂犬症,这是因为狂犬病毒特别容易侵犯狗而引起的,患上狂犬症的病狗一旦咬伤人,人就会感染狂犬病毒患上狂犬症,</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"line-height: 28px;\">但是狂犬症有一个潜伏期,可能当时不发病,但是如果不进行治疗,一定会患上狂犬病。而狂犬病的死亡率非常高,很难挽救。 那什么是狂犬病?会有哪些临床表现?我们一起来看一下。 狂犬病是由狂犬病毒所致的急性传染病,可以人兽共患,主要发生在一些食肉的动物,比如狗、狼、猫等,而最常见的就是狗和猫,狼等动物在日常生活中是很难接触到的,而狗和猫已经成为人们喜欢豢养的宠物,所以,狗和猫成为狂犬病的主要传染者,而人多因被病狗或者是病猫咬伤而感染。</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"line-height: 28px;\"><img src=\"http://p1.pstatp.com/large/2c610011bb74a59779b4\" img_width=\"640\" img_height=\"497\" style=\"border: 0px; display: block; max-width: 100%; margin-bottom: 10px;\"/><br/><br/></span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"line-height: 28px;\">因此,现在预防狂犬症最好的办法就是确定被咬伤以后立即打狂犬疫苗,只要是打狂犬疫苗可以90%控制狂犬病的发生。 那狂犬病的表现症状?</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"line-height: 28px;\">1、狂犬病发作之前往往有一个短暂的前驱期,这个时间会持续1至4天,主要的症状是表现为中度发热,全身感觉不适,食欲下降甚至消失,同时还伴有头痛、恶心等。症状类似感冒,因此,很容易被忽略。</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"line-height: 28px;\">2、过了前驱期之后就进入神经系统的症状期,这个症状会出现在2天至20天,主要的表现是应激性增高,感觉胸部有很强的压迫感,同时伴有胸痛及气流恐怖症,严重的当风吹面部时都会引起咽喉部肌肉的痉挛,进而影响呼吸和进食,这是狂犬病非常典型的症状之一。对临床判断有利。</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"line-height: 28px;\">3、局部症状。被病狗咬伤的伤口部位会有疼痛,还可能出现有麻木、痒痛的异常感觉,手脚四肢仿佛有蚂蚁在爬,非常痛苦。还有的病人同时伴有对各种光、噪音和感觉剌激的应激性增高,不愿意见光,也不愿意被触碰,而一旦受刺激,脸部的表情会非常的恐惧和恐怖,这是因为病毒侵犯神经表现出肌张力增高和面部肌肉痉挛所致。</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"line-height: 28px;\">4、交感神经系统病受损的症状。当交感神经受到损后,病人会出现多汗、流涎、狂躁等表现,而且表现出焦虑、痉挛性痛性肌肉收缩,而且大多数人在吞咽时引起咽喉等部位的肌肉痉挛所以很怕喝水,故又称恐水症。 狂犬病的死亡率非常高,所以千万不能掉以轻心,如果不慎被狗或者猫咬伤,就要立即去医院进行注射狂犬疫苗,千万不能像本文中的8岁的小女孩一样,白白的送上了性命。</span></p><p><a href=\"http://www.wukong.com/answer/6445019792632971533/?showComment=6445019792632971533\" target=\"_blank\" class=\"show-comment\" style=\"cursor: pointer; text-decoration-line: none; color: rgb(153, 153, 153); float: left; line-height: 16px; margin-left: 0px;\"><span class=\"y-icon icon-comments_details\" style=\"position: relative; display: inline-block; line-height: 1; font-size: 12px; color: rgb(202, 202, 202); -webkit-font-smoothing: antialiased; font-family: yFont !important;\"></span>&nbsp;10评论</a></p><p><a href=\"http://www.wukong.com/user/?uid=24629342802\" class=\"answer-user-avatar\" target=\"_blank\" style=\"cursor: pointer; text-decoration-line: none; color: rgb(153, 153, 153); width: 40px; height: 40px; border-radius: 50%; display: inline-block; position: relative; overflow: hidden; margin-right: 8px; float: left;\"><img class=\"\" src=\"http://p3.pstatp.com/thumb/2498000be9f82ea18e36\" alt=\"\" style=\"border: 0px; width: 40px; height: 40px;\"/></a><a class=\"answer-user-name\" href=\"http://www.wukong.com/user/?uid=24629342802\" target=\"_blank\" style=\"cursor: pointer; text-decoration-line: none; color: rgb(34, 34, 34); display: block; font-size: 14px; line-height: 20px; margin-top: 2px; white-space: nowrap; width: 490px; text-overflow: ellipsis; overflow: hidden; margin-bottom: 2px;\">萌萌萌萌猛嘉</a><a class=\"answer-user-tag\" style=\"cursor: pointer; color: rgb(153, 153, 153); display: block; margin-top: 2px; width: 620px; margin-bottom: 2px;\">07-20 20:16</a></p><p><span class=\"y-icon icon-digg\" style=\"position: relative; display: inline-block; line-height: 1; color: rgb(202, 202, 202); -webkit-font-smoothing: antialiased; margin-right: 4px; font-family: yFont !important;\"></span><span data-node=\"like-count\">345</span>赞</p><p><span class=\"y-icon icon-thumbsdown_normal\" style=\"position: relative; display: inline-block; line-height: 1; color: rgb(202, 202, 202); -webkit-font-smoothing: antialiased; margin-right: 4px; font-family: yFont !important;\"></span>踩</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">狂犬病发作表现一：性欲高涨频繁射精</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">　　北京一名32岁的男子，一天内出现频繁射精的现象，多达40—50次后，最终因抢救无效身亡。据悉，该男子并不是“精尽人亡”，检测发现其狂犬病病毒呈阳性，原来是死于狂犬病发作。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">　　专家表示，狂犬病导致患者出现性功能异常，可能是因为大脑结构受损有关。根据尸检报告，推测持续勃起和射精或许是狂犬病毒损伤了大脑结构引发的。此外，还有人认为，异常勃起和射精，可能是因为狂犬病毒入侵了脊髓腰骶段，产生了刺激性病灶，不断施放冲动使泌尿生殖系统工作异常。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">　<span style=\"font-weight: 700;\">　狂犬病发作表现二：一听到水就害怕</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">　　李大爷本是一家工厂的看门人，事发前，他常喂食厂门外的一条流浪狗。有一天李大爷被这只狗咬伤也没有在意，他只是在家简单地清洗了伤口。大约两个月后，他向家人提及此事时，已经出现了狂犬病的症状。他虽然口很干却喝不下水，只要有人提及“水”字他就会害怕，老人临去世时麻药已经对他不起作用了。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">　　狂犬病毒具有嗜神经性，病毒自中枢神经系统向周围神经离心性扩散侵入各组织与器官，其中尤以唾液神经核舌咽神经核和舌下神经核受损，临床上可出现恐水、呼吸困难、吞咽困难等症状。狂犬病患者就算是口渴，也会因为喉咙痉挛而喝不了，甚至想到水就害怕。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">　　<span style=\"font-weight: 700;\">狂犬病发作表现三：风一吹就发抖</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">　　金阿姨和丈夫在家吃饭，突然一只流浪狗跑了进来，金阿姨上前挥赶，谁知小狗竟一口咬伤阿姨的手，想着就流了<span style=\"line-height: 28px; text-decoration-line: underline;\">一点血</span>，就没去医院直接将<span style=\"line-height: 28px; text-decoration-line: underline;\">创可贴</span>，以为事情就过去了，但谁也没想到这才是噩梦的开始。大约过2个月后，在农田里干活的金阿姨突然觉得有些胸闷，偶尔有风吹来就愈发不适。经过几天的折磨，金阿姨由于呼吸系统衰竭，她离开了人世。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">　　因为神经受损，患者可出现相应神经支配的功能异常症状，比如吞咽及呼吸困难、怕光怕水怕风、唾液分泌增多及情绪波动、高热、头痛、<span style=\"line-height: 28px; text-decoration-line: underline;\">呕吐</span>、腱反射消失、<span style=\"line-height: 28px; text-decoration-line: underline;\">瘫痪</span>等神经系统症状。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"font-weight: 700;\">　　狂犬病发作表现四：潜伏期可长可短</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">　　28岁的男子张伟平时身体一直很好，膝下有一双女儿，日子过得安稳惬意，但在今年6月4日，他突然狂躁不安，在家里见什么摔什么，家人于是将其带至淮阴县医院进行救治，病情不见好转又转至淮安市第四人民医院，后被医院诊断为狂犬病，直到此时，家人才回想起，张伟在其5岁时确实被狗咬过，但没想到这个病竟潜伏在其体内23年之久，6月6日晚11时许，潜伏在其体内23年的狂犬病病毒最终要了张伟的性命。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">　　狂犬病潜伏期可长可短，10天至1年都有，甚至又长达20年之久，但发病时却是来势汹汹。狂犬病发病后病程特别短，从出现症状到死亡仅仅6天左右，最长不超过10天左右。这是因为，病毒进入大脑完全破坏神经仅仅需要24小时，没有任何药剂可以在几天内不损害中枢神经系统的方式<span style=\"line-height: 28px; text-decoration-line: underline;\">下消</span>灭病毒并<span style=\"line-height: 28px; text-decoration-line: underline;\">修补</span>神经。这就是为什么狂犬病患者一旦发病几乎100%死亡的原因，但狂犬病的潜伏期则可长可短。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">以上就是狂犬病发作的几种奇葩表现，想要远离狂犬病，平时就要少接触患病的犬类或猫科动物，一旦被咬及时打狂犬疫苗，以免后患。</p><p><br/></p>', '1', '1', '0', '健康网', '凌寒', '', '1500618940', '1500618940', '2', '', '');
INSERT INTO `news_article` VALUES ('6', '降压药加每天1万步3年，为何血压降不好？', '1', '20170721\\4f299a450f77fb2f837a26d0d483cb2f.jpg', '余千兰：你能告诉我，你年龄、没服药时最高血压多少？体检结果？以前患过什么疾病？都服过哪些药？身高、体重？', '降压药，运动', '<p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">网友：医生、你好！我一直吃氨氯地平贝那普利片，每天一片，效果一般，怎么办？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">余千兰：你能告诉我，你年龄、没服药时最高血压多少？体检结果？以前患过什么疾病？都服过哪些药？身高、体重？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ecd000367ede1381e2e\" img_width=\"1280\" img_height=\"851\" alt=\"降压药加每天1万步3年，为何血压降不好，运动不能降血压吗？你别不相信\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">网友：身高183，体重89kg，没有得过什么病，体检结果很好，服降压药前背了一天动态血压，就开始吃降压药了。早起后测量血压一般在149/96左右。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">余千兰：你体重指数26.6（体重公斤/身高平方米），正常人小于24，你有点偏胖，建议减肥有利于血压下降。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">你是什么工作？现在血压偏高，抽烟喝酒吗？生活规律吗？，检查过同型半胱胺酸的检测吗？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">网友：就是抽烟！没有做这个检测，晚上睡得晚！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">余千兰：好的，你目前血压药配得可以，只要戒烟，加强运动，减肥，少熬夜，这样降压效果可以增强的。可以检查下血同型半胱胺酸。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ed700013d5346b4adc9\" img_width=\"1280\" img_height=\"853\" alt=\"降压药加每天1万步3年，为何血压降不好，运动不能降血压吗？你别不相信\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">网友：每天我走一万多步，已坚持3年了！每天一片量够吗？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">余千兰：可以的，服药时注意测量血压。运动降压只是一方面 ，运动可以使血压降低大约4~9mmHg。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">如果说影响你的血压不光是运动，有时效果不佳，观察影响血压的原因，饮食习惯？高钠低钾（重口味）？体重？熬夜？心情？没有戒烟？等等，有的人抽烟可能不影响血压或血压不高，但是针对个体，你就是不该抽烟，一抽就影响血压，谁让遗传基因抵抗不了呢？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">网友：医生，这个类型药每天一片量是不是少了一点？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">余千兰：不少，是联合用药，故比单用要少。关键是服后血压情况，观察。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">网友：测量血压还是早餐前测量吗？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ed50002c148b75039b8\" img_width=\"1280\" img_height=\"755\" alt=\"降压药加每天1万步3年，为何血压降不好，运动不能降血压吗？你别不相信\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">余千兰：若血压不稳定，早晚都要测量，若稳定，早餐前测量。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">网友：医生，测后再服药 对吗？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">余千兰：可以。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">上面的网友不能改善生活方式，可能要加服药， 但不是加量，是加利尿剂（没有禁忌证），这样不是最好的办法。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">高血压病友在管理自己的高血压时，到底什么 时候测血压好？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">1，每天睡醒时即测量血压。此时的血压水平能反映药物降压作用的持续效果、夜间睡眠时的血压情况。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2，高血压病友，在刚开始服降压药时，或换服药时，应该每隔数小时测量1次，或进行24小时动态血压监测，以确定换药的方案是否适合自己。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ed50002c1b6bb1db8c8\" img_width=\"1280\" img_height=\"1273\" alt=\"降压药加每天1万步3年，为何血压降不好，运动不能降血压吗？你别不相信\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">3，服降压药后2~6小时测量血压。因为降压药作用高峰分别在服药后2~6小时出现，此时测量血压可以反映药物的最大降压效果。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">4，大多数人的血压明显呈昼夜节律性，即白天血压较高，夜间睡觉血压较低，呈双峰一谷现象，即上午6~10时及下午4~8时，在这两个时段测量血压，可以了解1天中血压的最高点，防心脑血管意外的发生。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">5，定时服降压药，不要随意减量或停药，若有必要，建议在医生指导下评估病情调整。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">6，若血压稳定，可以定期测量血压，1~2周可以测量1次。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">7，最好自备血压计并学会自测血压，不要为测血量压而焦虑，感觉不适时提醒自己测量血压。多和你的医生交流，让你的诊疗方案能控制好血压到目标值（＜140/90mmHg）以下，才能真正保护好全身血管！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2ed50002c26140888505\" img_width=\"1280\" img_height=\"853\" alt=\"降压药加每天1万步3年，为何血压降不好，运动不能降血压吗？你别不相信\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">真的，高血压治疗就是：心态+生活不良方式改善+坚持服降压药，心态和改善不良生活方式是降高血压的基石，降压药物是治疗高血压的保障！</p><p><br/></p>', '1', '1', '0', '健康网', '凌寒', '', '1500619057', '1500619057', '2', '', '');
INSERT INTO `news_article` VALUES ('7', '远离性侵，务必让你的孩子知道“懂得、明确、不准”', '1', '20170721\\09a137181dee4f04b71109cfba4b61f9.jpg', '性教育，是家庭教育重要的分支，是家长和孩子和谐相处，无障碍沟通交流的重要内容，也是家长智慧教子的美好体验。', '性侵，三懂得，三明确，三不准', '<p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ee50000e4e2b0574b84\" img_width=\"1280\" img_height=\"881\" alt=\"远离性侵，务必让你的孩子知道“三懂得”、“三明确”、“三不准”！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><hr style=\"margin: 40px auto; padding: 0px; width: 64px; height: 4px; background: rgb(237, 64, 64); outline: 0px; border: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal;\"/><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">最近网上看到一则消息“老师又来盖被子了”。报道的是广西平南县思旺镇，某托管机构的多名女童近日指认，一名中学男教师谭某，利用兼职查宿机会，长期对她们作出“不雅举动”。<br/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">根据受访女童的反映，该举动至少包括猥亵，最早的一次可追溯到4年前。事发后，谭某被刑拘接受进一步调查。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">另一则消息，南京一位母亲将10岁女儿放在书店自己外出，一会回来正看见一个70岁的老人对玩电脑的女儿动手动脚。母亲愤而报警，不规矩的老人被拘留。事后问女儿知不知道那人在对你做坏事，女儿懵懵懂懂地说：“感觉到了，可是不知他在做什么。”</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">侵犯在发生，我们的女儿却毫不知觉，或者有所知觉却放纵容忍！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">这两个案件反映了对孩子性安全教育的严重缺失。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">由于家长不懂得不屑于或者羞于对孩子进行性安全教育，导致孩子对于自己的身体缺乏权利意识，对性侵的概念模糊不清，对如何远离性侵，应对性侵缺乏足够的常识和</span><span style=\"font-weight: 700;\">自我保护意识</span><span style=\"font-weight: 700;\">，使得坏人的侵害屡屡得逞。</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">韩国电影《素媛》，讲述了一个8岁女孩在遭遇性侵后，如何在家人帮助下，走出心灵阴影面对生活的故事，这是根据韩国一个真实事件改编而成。<br/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">一个雨夜上学途中，素媛遇到一个醉酒大叔，以“合伞”为由，将素媛骗至偏僻小屋，实施了毫无人性的侵犯。备受摧残的素媛被装上“人工肛门”，父亲帮助她清理排便时，她惊恐万状、歇斯底里，所有男性信息特征都会令她恐惧、愤怒和排斥。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">影片中素媛在爱里得到新生，而现实中，故事原型的遭遇比素媛还要悲惨。创伤不仅仅摧毁了她的肉体，更造成女孩一生走不出心理阴影。对自己的厌恶，对人性的怀疑，对这个世界的冷漠与憎恨。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ee60000db6a223f7a13\" img_width=\"765\" img_height=\"510\" alt=\"远离性侵，务必让你的孩子知道“三懂得”、“三明确”、“三不准”！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">一个渣滓的偶尔冲动，制造了一起刑事案件，却造成了一个家庭的崩溃和一个女孩一生幸福的毁灭。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">家长千防万防，总有顾及不到的时候。教会孩子及时识别，断然规避和巧妙应对，才是保护孩子的唯一正确途径。</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">良好愿望+基本常识灌输+具体细节演练=护孩子一世周全</span><span style=\"font-weight: 700;\">。</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">问卷调查显示，家长对孩子的性安全教育，存在着三个顾虑，即：不知何时说，不知说到什么程度，唯恐诲淫诲盗。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">其实，这些担心很没必要。对孩子的性教育，应该跟孩子饮食安全、健康教育同时进行。比如，一个妈妈坚持不给自己的孩子穿开裆裤，她对孩子说，排大小便的地方属于隐私，不能裸露在外面。自然而然就把隐私概念引出来了。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">再比如，男孩子三周岁以后，妈妈上厕所换衣服时要关门，并规定孩子进门需要敲门征得同意后方可进入，这就引出了男女有别的意识。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">性教育尺度上，不必用成年人眼光看待 。对于孩子来说，性器官跟人的眼睛鼻子一样只是个器官，大大方方讲出名称和功用，孩子就像其他器官一样平静接受。相反，扭扭捏捏躲躲闪闪，才会引发孩子的好奇。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">对孩子的性教育越早越好。在孩子纯洁如白纸时，在日程生活点点滴滴，在看似无意识的点化中，自然而然，润物无声地将性安全知识灌输给孩子。当孩子遇到坏人欲行不轨时，会立刻警醒，采取必要的防护措施，避免侵害发生。</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2edb00025f13b192a85a\" img_width=\"1280\" img_height=\"1036\" alt=\"远离性侵，务必让你的孩子知道“三懂得”、“三明确”、“三不准”！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">如何对孩子进行性安全教育。本人总结概括为“三懂得”、“三明确”、“三不可以”。</p><h1 style=\"margin: 36px 0px 22px; padding: 0px; font-size: 24px; line-height: 32px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">“三懂得”</h1><ol class=\" list-paddingleft-2\" style=\"padding-top: 20px; padding-right: 30px; padding-bottom: 20px; list-style-position: outside; list-style-image: initial; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><li><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\">懂得身体是自己的，别人无权触碰；</p></li><li><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\">懂得成年人触碰自己背心裤衩覆盖的地方，是不道德行为；</p></li><li><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\">懂得反抗成年人对身体的侵犯不算没礼貌，为保护自己说谎不算犯错误。</p></li></ol><h1 style=\"margin: 36px 0px 22px; padding: 0px; font-size: 24px; line-height: 32px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">“三明确”</h1><ol class=\" list-paddingleft-2\" style=\"padding-top: 20px; padding-right: 30px; padding-bottom: 20px; list-style-position: outside; list-style-image: initial; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><li><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\">明确性侵和猥亵的部位和尺度（孩子没有成人那样敏感龌龊），让孩子区分喜爱与侵犯的不同；</p></li><li><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\">明确男女大防，异性长辈手足，在穿衣便溺居住方面有所避讳；</p></li><li><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\">明确父母永远是你的靠山。无论闯了多大的祸，记得跟父母求助，不受坏人要挟，做自己不喜欢的事。</p></li></ol><h1 style=\"margin: 36px 0px 22px; padding: 0px; font-size: 24px; line-height: 32px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">“三不能”</h1><ol class=\" list-paddingleft-2\" style=\"padding-top: 20px; padding-right: 30px; padding-bottom: 20px; list-style-position: outside; list-style-image: initial; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><li><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\">女孩子外出，不能走单，有权利要求成年人陪送；</p></li><li><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\">女孩与异性不能单独长时间共处一室，一车;</p></li><li><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\">女孩子面对陌生人的求助，搭讪，坚决说不，有事找警察，不做烂好人。</p></li></ol><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">“三懂得”强调的是权利，“三明确”强调的是界限，“三不能”强调的是行为。三方有机结合，形成孩子高度敏感高度警觉的思维和行为模式。在百毒不侵，无孔可入的孩子面前，坏人也会怂的。</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">性教育，是家庭教育重要的分支，是家长和孩子和谐相处，无障碍沟通交流的重要内容，也是家长智慧教子的美好体验。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">任何担忧和顾虑都是多余的。</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">任何探索和实践都值得点赞。</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">因为，保护孩子，就是保护我们的安详生活和美好未来</span>。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p9.pstatp.com/large/2ee200025edde036c2c1\" img_width=\"1280\" img_height=\"1280\" alt=\"远离性侵，务必让你的孩子知道“三懂得”、“三明确”、“三不准”！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p><br/></p>', '1', '1', '0', '健康网', '凌寒', '', '1500619233', '1500619233', '2', '', '');
INSERT INTO `news_article` VALUES ('8', '产检被医生1个问题羞红了老脸，真好尴尬啊', '1', '20170721\\45106c08ce84075aad7d446061256eff.jpg', '产前检查顾名思义为生产前的检查，又称围产保健，能及时了解孕妇身体情况及胎儿的生长发育情况，保障母亲和胎儿的健康和安全，做到优生优育。', '产检，女性', '<p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">产前检查顾名思义为生产前的检查，又称围产保健，能及时了解孕妇身体情况及胎儿的生长发育情况，保障母亲和胎儿的健康和安全，做到优生优育。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">常规检查：测量血压和体重，是每次产前检查的保留项目;而首次检查这两项的结果，有助于医生掌握准妈妈的健康状况，如果以后某次检查孕妇的血压或体重上升得比较快，医生就会有所警觉，并采取相应的方法解决。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">为了生个健康的宝宝，孕期各种各样的产检我们是逃不掉的，产检的次数多了，难免会遇到让人尴尬的事。例如：医生问，以前有流产史吗？这时候如果没有还好说，如果有，呵呵！回家绝对又是一场腥风血雨。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">记得我有一次去医院产检，是老公陪着我去的，刚进门坐下没多久，那位看起来有些年纪的女医生扭头就说，孕妇留下就可以了，麻烦家属先出去。她是成年人了，在这里我可以保证她的安全，请在门口耐心等待！然后老公说了句我在外面等你！就出去了。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/18aa0005cd21a5bfb792\" img_width=\"320\" img_height=\"240\" alt=\"产检被医生1个问题羞红了老脸，真好尴尬啊\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">常规检查：测量血压和体重，是每次产前检查的保留项目;而首次检查这两项的结果，有助于医生掌握准妈妈的健康状况，如果以后某次检查孕妇的血压或体重上升得比较快，医生就会有所警觉，并采取相应的方法解决。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">当时不知道是怎么回事，默默地等着医生问话，医生一开口就把我吓着了，她很和蔼地说：小姑娘，以前有过流产史吗？我愣了一下，感觉有些难以启口。这时候只有我们两个在一个房间，我也只能硬着头皮说了。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">“以前流过一个。”我说。医生倒没什么异样的表情，还是很和蔼地继续问了一下话，检查了一下身体，记了些东西就让我出去了。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/18ab0005523cec7c24a7\" img_width=\"461\" img_height=\"306\" alt=\"产检被医生1个问题羞红了老脸，真好尴尬啊\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">后来我才知道医生问有没有流过是为了了解我的身体状况。听在妇产科工作的同学说，有流产史的孕妇，因为之前的子宫内壁有损伤，孕期更容易发生胎盘粘连，产后大出血的风险也会比常人高。如果有过胎停育、畸形胎儿或是自然流产的，医生就会特别注意这胎。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">妊娠不足28周、胎儿体重不足1000g而终止妊娠者称流产。流产发生于妊娠12周前者称早期流产，发生在妊娠12周至不足28周者称晚期流产。流产又分为自然流产和人工流产，自然流产的发病率占全部妊娠的15%左右，多数为早期流产。还有两种特别流产类型：稽留流产和习惯性流产。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">通常在孕期，妇科医生是没有办法看出孕妇有没有流过产的，只有通过检查才能知道。但为了胎儿健康着想，还是建议孕妈不要隐瞒，如实向医生交待。尤其是之前有过自然流产或习惯性流产的孕妈。如果怕家人在不方便回答，就先说没有。等生孩子的时候再告诉医生以前流过，如果遇到什么突发状况医生也能及时补救。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/18aa0005cd20d5871242\" img_width=\"384\" img_height=\"240\" alt=\"产检被医生1个问题羞红了老脸，真好尴尬啊\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">早期自然流产时，染色体异常的胚胎占50%～60%，多为染色体数目异常，其次为染色体结构异常。数目异常有三体、三倍体及X单体等；结构异常有染色体断裂、倒置、缺失和易位。染色体异常的胚胎多数结局为流产，极少数可能继续发育成胎儿，但出生后也会发生某些功能异常或合并畸形。若已流产，妊娠产物有时仅为一空孕囊或已退化的胚胎。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">不过好像并不是所有孕妈都像我能遇到这么负责的医生，之前听一个孕妈说以前自己年轻不懂事，有了孩子又没有能力养，又害怕别人说闲话就把孩子给打掉了，后来又怀了一胎，但是当时男朋友却和另外一个女的好上了，最后也只能打掉了。后来她遇到了现在的老公，对她还不错，但是婆婆有点不喜欢她，也不知道为什么。后来婆婆和她一起去产检，好死不死地医生也不避讳地问了这个问题，那时候尴尬地只想找洞钻了。最后她如实告诉医生流过两个。婆婆听到后对她的态度更是一落千丈，在家老给她脸色看，也是可怜。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ee10003fa52fe4c8f15\" img_width=\"1160\" img_height=\"1280\" alt=\"产检被医生1个问题羞红了老脸，真好尴尬啊\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">流产过的也不用担心，现在很多医生在产检时都会先让家人在外面等候，即便有过流产经历回答也不会太尴尬。</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">妊娠早期时，除卵巢的妊娠黄体分泌孕激素外，胎盘滋养细胞亦逐渐产生孕激素。妊娠8周后，胎盘逐渐成为产生孕激素的主要场所。除孕激素外，胎盘还合成其他激素如β-绒毛膜促性腺激素、胎盘生乳素及雌激素等。早孕时，上述激素值下降，妊娠难以继续而致流产。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">妊娠犹如同种异体移植，胚胎与母体间存在复杂而特殊的免疫学关系，这种关系使胚胎不被排斥。若母儿双方免疫不适应，则可引起母体对胚胎的排斥而致流产。有关免疫因素主要有父方的组织相容性抗原、胎儿特异抗原、血型抗原、母体细胞免疫调节失调等。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">流产的主要症状是阴道流血和腹痛。阴道流血发生在妊娠12周以内流产者，开始时绒毛与蜕膜分离，血窦开放，即开始出血。当胚胎完全分离排出后，由于子宫收缩，出血停止。早期流产的全过程均伴有阴道流血；晚期流产时，胎盘已形成，流产过程与早产相似，胎盘继胎儿娩出后排出，一般出血不多，特点是往往先有腹痛，然后出现阴道流血。流产时腹痛系阵发性宫缩样疼痛，早期流产出现阴道流血后，胚胎分离及宫腔内存有的血块刺激子宫收缩，出现阵发性下腹疼痛，特点是阴道流血往往出现在腹痛之前。晚期流产则先有阵发性子宫收缩，然后胎盘剥离，故阴道流血出现在腹痛之后。流产时检查子宫大小、宫颈口是否扩张以及是否破膜，根据妊娠周数及流产过程不同而异。</p><p><br/></p>', '1', '1', '0', '健康网', '凌寒', '', '1500619321', '1500619321', '2', '', '');
INSERT INTO `news_article` VALUES ('9', '涨知识：西瓜这样挑，才能知道它熟不熟、好不好吃！', '1', '20170721\\de9341b1e1e6353410f41746b1a7c856.jpg', '要说有什么水果能够代表夏天的，那一定是西瓜君莫属啦，甜甜的冰镇西瓜，一口下去就把所有燥热驱散了……  然而，想法很完美，现实却很无奈...因为买西瓜的时候，很多人不知道怎么判断它熟不熟、甜不甜，结果买回来的时候又生又淡，那就很尴尬了。', '西瓜，熟不熟', '<h1 style=\"margin: 36px 0px 22px; padding: 0px; font-size: 24px; line-height: 32px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">西瓜君挑选日记</h1><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">要说有什么水果能够代表夏天的，那一定是西瓜君莫属啦，甜甜的冰镇西瓜，一口下去就把所有燥热驱散了……</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">然而，想法很完美，现实却很无奈...因为买西瓜的时候，很多人不知道怎么判断它熟不熟、甜不甜，结果买回来的时候又生又淡，那就很尴尬了。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">氮素，别担心！有小鲜在，怎么可能让大家发生这种事呢，接下来我就来跟大家说说，如何判断哪些西瓜更熟更好吃！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ed20004ce510925681b\" img_width=\"450\" img_height=\"325\" alt=\"涨知识：西瓜这样挑，才能知道它熟不熟、好不好吃！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><h1 style=\"margin: 36px 0px 22px; padding: 0px; font-size: 24px; line-height: 32px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">开讲</h1><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">01</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">买西瓜的时候，用手掌轻拍或用手指弹西瓜，熟瓜会发出“嘭嘭”的声音，生瓜会发出“当当”声音，而过熟的瓜则是“噗噗”声。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2edd0004dbc77ea29f49\" img_width=\"600\" img_height=\"398\" alt=\"涨知识：西瓜这样挑，才能知道它熟不熟、好不好吃！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">02</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">同样大小的两个西瓜，熟的西瓜比较轻而生的比较重，熟瓜放入水中会浮起来，生瓜放到水中会沉下去。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2edd0004e9d656868545\" img_width=\"750\" img_height=\"562\" alt=\"涨知识：西瓜这样挑，才能知道它熟不熟、好不好吃！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">03</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">瓜皮颜色淡、青，还有细白毛的，一般来说还是比较生的瓜；成熟的西瓜瓜皮比较光滑、发亮。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ecf0004b364ad9f96a9\" img_width=\"782\" img_height=\"401\" alt=\"涨知识：西瓜这样挑，才能知道它熟不熟、好不好吃！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">04</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">如果有小伙伴仔细看过西瓜，就发现西瓜的纹路是不一样的。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">纹路明显、表皮光滑、体型匀称的，</span>西瓜一般比较好吃，而尖头歪脑、表皮黏黏软软的，肯定不好吃。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">06</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">还有一点估计平时大家都不太会注意到，西瓜的柄是有弯曲和笔直的之分的，柄较弯曲的西瓜会比较甜，柄较直的西瓜没这么好吃。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ed500040eb65f6ccc57\" img_width=\"630\" img_height=\"396\" alt=\"涨知识：西瓜这样挑，才能知道它熟不熟、好不好吃！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">06</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">西瓜在生长过程中，一部分压在地面上形成了一个白圈，如果西瓜不是自然成熟的，那个白点不会那么明显，而好的西瓜是非常明显的。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">07</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">最后，西瓜底部的有个小圆圈，圆圈越小越好，底部的圆圈越大西瓜的皮就越厚，瓜就没这么好吃啦。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2ed600028984bc546b22\" img_width=\"800\" img_height=\"506\" alt=\"涨知识：西瓜这样挑，才能知道它熟不熟、好不好吃！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">好啦，就素酱紫，根据以上技巧，相信大家都能够买到好吃的西瓜啦。</p><p><br/></p>', '1', '1', '0', '健康网', '凌寒', '', '1500619502', '1500619502', '2', '', '');
INSERT INTO `news_article` VALUES ('10', '女人为什么离不开男人？夫妻间谁更需要谁？', '4', '20170721\\40c7bf8eef0e9be4860009d1410a2b72.jpg', '1、小伙子心仪一姑娘很久，于是借机找她帮了个忙。事成之后，小伙子说：“我该怎么感谢你呢，我娶你吧！”姑娘仔细看了看他的模样说：“你怎么能恩将仇报呢？”  2、一女同事忧心忡忡的问我：我家的牛每次才耕一会田就没力气了，怎么办？', '夫妻，女人，男人', '<p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">开心一刻：</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">1、小伙子心仪一姑娘很久，于是借机找她帮了个忙。事成之后，小伙子说：“我该怎么感谢你呢，我娶你吧！”姑娘仔细看了看他的模样说：“你怎么能恩将仇报呢？”</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2、一女同事忧心忡忡的问我：我家的牛每次才耕一会田就没力气了，怎么办？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">我不假思索的回答道：卖牛肉吧！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">结果女同事长叹一声：哎。。。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">总觉得哪里不对，她家住21楼，怎么养牛？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p9.pstatp.com/large/2ee4000330c61fc2391a\" img_width=\"500\" img_height=\"386\" alt=\"女人为什么离不开男人？夫妻间谁更需要谁？\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">3、丈夫说：有个一举四得的事情，咱做不做？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">妻子说：做呀。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">丈夫说：咱请个保姆吧，我走了，她陪你，你走了，她陪我，咱们都走了，她为咱收拾家，咱们都在家，她为我们洗衣、做饭。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2、儿子午睡起床，老婆喂水，儿子不肯喝，拉扯了半天还是不喝，老婆怒了，“你又不是三岁小孩，拽什么啊，不喝拉倒。”然后就撇下我们父子俩走了。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">我无辜地看着儿子，儿子一脸无邪地望着我，宝贝啊，你妈没说错，你不是三岁小孩，你才一岁半……</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">男追女，隔座山，女追男，隔成纱！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">为啥女人离不开男人？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">对于已婚女士，丈夫对她最好的宠爱就是亲密的关系，执子之手与子偕老！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">无论是成婚一年，还是十年二十年，每当他牵起你的手，他都依然冲动的像个少年——这是女人的梦想。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">然而，现实往往有些残酷，婚姻的七年之痒让很多男人违背了当初的承诺！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ee60001b1d447bd1849\" img_width=\"640\" img_height=\"446\" alt=\"女人为什么离不开男人？夫妻间谁更需要谁？\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">其实，女人离不开男人，不仅仅是因为爱，最近，美国的科学家给出了以生理学为基础的研究成果，证明女人离不开男人，因为经常有男人宠爱的女人，对女性的身体有很多的好处：</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">1、有助减肥</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">据调查显示，一个热烈的接吻燃烧十二卡路里，30分钟的亲密.运动可以燃烧200卡路里。如果在过程中比较兴奋和热情，消耗的能量更多。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2、皮肤红润</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">人面桃花，说的就是新婚燕尔的新娘子。刺激和运动可以促进肾上腺素的产生，让人看起来气色更好，这可是任何化妆品都比不了的。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">反之，如果长时间不和伴侣进行运动，则皮肤血液循环不良，会导致粉刺、暗疮等皮肤病。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2ee200032f4b324e999a\" img_width=\"498\" img_height=\"370\" alt=\"女人为什么离不开男人？夫妻间谁更需要谁？\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">3、助眠</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">如果有一场酣畅淋漓的运动，可以释放促进睡眠的内啡肽，让夫妻迅速进入甜美的梦乡。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p9.pstatp.com/large/2ee50001b824fe1ae977\" img_width=\"620\" img_height=\"402\" alt=\"女人为什么离不开男人？夫妻间谁更需要谁？\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">夫妻之间的亲密关系，你属于哪种？一周一次，一周三次or一周五次或更多？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">我相信如果一周连一次都没有，女方也会崩溃的。如果男人总以太忙了，太累了为借口，女方最先怀疑的，就是你的身体或者是心里有了别人。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p9.pstatp.com/large/2ee10003e210a9d6db4c\" img_width=\"640\" img_height=\"427\" alt=\"女人为什么离不开男人？夫妻间谁更需要谁？\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">那么，如果频率不达标，吃什么有助于提高男人的体力呢？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">1、多不饱和脂肪酸——欧米伽3</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">医学研究表明，人类普遍缺乏欧米伽3，这是导致人体脂肪酸摄入不均衡的重要原因，也是引起肥胖、免疫力低下，高血脂，高血压等慢性疾病的重要因素，因此，补充欧米伽3亚麻酸，有助于增强免疫力，降低血脂，预防心脑血管疾病。欧米伽3是人体必需脂肪酸，存在于深海鱼和少数特殊的食用油中，例如苏麻油中欧米伽3亚麻酸含量高达65%，是世界上目前所知欧米伽3含量最高的食物。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">食用方法：苏麻油拌酸奶，把苏麻油5毫升加到100-200毫升酸奶里搅拌均匀食用即可，或者每天口服5毫升，或加到做好的菜里食用。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2ee10003e2131c2b0ff5\" img_width=\"2309\" img_height=\"1731\" alt=\"女人为什么离不开男人？夫妻间谁更需要谁？\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2、丹芝粉</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">丹芝是灵芝中的上品，对增强人体免疫力有很好的作用。失眠多梦，免疫力低下，容易感冒，脂肪肝，这些是中年男性常见问题，遇到这些问题，可以食用丹芝粉，丹芝是灵芝的一种，色泽棕红色，被称为丹芝，是灵芝中药效最好的品种，含有多糖和三萜类成分，有很好的安神和增强免疫力，抗肿瘤的作用。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ee4000330c7ae0dd6dd\" img_width=\"588\" img_height=\"436\" alt=\"女人为什么离不开男人？夫妻间谁更需要谁？\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">3.蒲公英包谷须茶</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">配方：蒲公英2g、包谷须1g、玫瑰0.5g、玉竹0.5g</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">糖尿病是男性最常见的疾病，一般伴有肥胖等，严重的糖尿病也是导致男性阳痿的一个重要原因。包谷须也叫玉米须，中医认为玉米须含有多糖和黄酮类成分，对降低血糖有帮助，而蒲公英则能去火消炎，利尿，排毒，用蒲公英和包谷须配制成茶，有降低血糖，预防糖尿病的作用。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ee50001b82587f1c852\" img_width=\"640\" img_height=\"360\" alt=\"女人为什么离不开男人？夫妻间谁更需要谁？\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">4、南瓜子油</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">男性最常见的生殖系统问题就是前列腺增生，前列腺增生容易导致尿频尿急，排尿困难，尿不尽等，十分影响男性的形象，同时严重的也会导致射精困难，因此有前列腺增生或者尿频尿急的男性，可以服用南瓜子油。欧洲及美国的研究表明，南瓜子油含有一种特殊的植物甾醇类物质，类似男性荷尔蒙，可以抑制前列腺的初期肿胀，对男性尿频尿急，排尿困难效果很好。每天5毫升直接口服，或者淋到做好的菜里食用，也可以用南瓜子油拌酸奶，据说能让男人更强大。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ee200032f4a2bc8619b\" img_width=\"640\" img_height=\"401\" alt=\"女人为什么离不开男人？夫妻间谁更需要谁？\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">当然，除了食补调理外，男人平常多做体育运动，练习俯卧撑等也有助于提高体能，延长时间。</p><p><br/></p>', '1', '1', '1', '健康网', '凌寒', '', '1500619645', '1500619645', '2', '', '');
INSERT INTO `news_article` VALUES ('11', '三伏天，40天的治病黄金时段，每天坚持喝它一碗.', '5', '20170721\\91a186153edec013ef39106ee185af0a.jpg', '大家都估计听说过“冬病夏治”，恰好的40天治病黄金时段，就在这个夏天。现在已经入伏好几天，湿气也开始嚣张起来了。小编看了一下身边的人，流鼻涕的，打喷嚏的，戴口罩的。看来难道夏天跟流感更配吗？然而传统医学认为，夏秋之交热而多雨，湿气较重。湿气重就容易生病。', '三伏天，治病，湿气', '<p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">大家都估计听说过“冬病夏治”，恰好的40天治病黄金时段，就在这个夏天。现在已经入伏好几天，湿气也开始嚣张起来了。小编看了一下身边的人，流鼻涕的，打喷嚏的，戴口罩的。看来难道夏天跟流感更配吗？然而传统医学认为，夏秋之交热而多雨，湿气较重。湿气重就容易生病。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2ee200029dff36ecf21b\" img_width=\"997\" img_height=\"577\" alt=\"三伏天，40天的治病黄金时段，每天坚持喝它一碗，半个月湿气全无\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">三伏天，不知道是福还是祸，虽然身体很容易就被侵袭，但三伏天却是“冬病夏治”的黄金时段。三伏时节，不仅是人体阳气最足的时节，同时，在三伏时节。人体的毛孔是处于张开的状态，血液循环也较其他时节快，新陈代谢也加快了。这时候，不论是针灸、拔罐、敷贴驱寒，都会起到事半功倍的效果。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">所以中医专家们提出“三伏补阳祛湿治病，三九滋阴护阳防病“。那我们就来聊聊，三伏天应该吃什么？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2ee20002b10535e5c4a4\" img_width=\"480\" img_height=\"278\" alt=\"三伏天，40天的治病黄金时段，每天坚持喝它一碗，半个月湿气全无\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">一、吃清热解暑、健脾利湿食物</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">暑天湿气重，我们应该多吃一些凉性，苦味的食物，有助于体内的湿气毒素排出来。例如：苦瓜，薏米，而薏米又名薏苡仁，是常用的中药。又是普遍、常吃的食物，具有味甘淡微寒,有利水消肿、健脾去湿、舒筋除痹、清热排脓等功效。我们可以用它来煮粥，又或者泡水喝！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">薏米枸杞茶</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">做法：取若干颗红枣去核放入茶壶中，再加入适量枸杞和冰糖，放入炒好的薏米，倒入开水，浸泡十分钟左右，每天坚持喝一杯，就能达到利水消肿，健脾祛湿的效果。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">薏米红豆粥</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">材料：红豆25克、薏米30克、枸杞18克、芡实 20克</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">做法：一把红豆，一把薏米洗净，睡觉前浸泡上，早上起来用电高压锅煮大概15分钟就好，时间到锅会自动停止，简单的薏米红豆去湿粥就做好了。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ee100035f8ae01d93e3\" img_width=\"292\" img_height=\"324\" alt=\"三伏天，40天的治病黄金时段，每天坚持喝它一碗，半个月湿气全无\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">二、吃益气养阴，补血润肺的食物</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">大暑天气酷热，出汗较多，容易耗气伤阴，应常吃一些益气养阴且清淡的食物以增强体质。如山药、大枣、海参、鸡蛋、牛奶、蜂蜜、莲藕、木耳、豆浆、百合粥、菊花粥等。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p9.pstatp.com/large/2ee20002b3ac71907654\" img_width=\"413\" img_height=\"451\" alt=\"三伏天，40天的治病黄金时段，每天坚持喝它一碗，半个月湿气全无\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">三、多吃新鲜的蔬果，少吃凉菜或油炸性食物</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">夏天水果缤纷上市，但水果可不能乱吃，不能过量地吃一些当季的水果，因为夏天的水果多半寒湿，所以注意要吃一些健康的新鲜的水果，例如：苹果，香蕉，雪梨，葡萄等等。晚上的凉菜也不能吃，因为凉菜放在冰箱是会滋生细菌的。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">三伏天，40天的治病黄金时段</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">这时我们就要为冬天的病做好预防准备了</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">1、适当的运动，早上不宜太早跑步，因为夏天的清晨，雾气湿重，很容易导致人着凉的。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2、泡茶养生，翠钱茶是一种养生祛湿消水肿的茶，其实翠钱就是我们日常生活中的桑叶，桑叶具有消肿降血脂的功效，在夏天，我们可以去药店买一些翠钱泡茶喝，有助于养生祛湿，保健康。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">3、晚上的时候，我们可以用姜水泡一下脚，脚底有许多我们不知道的穴位，用姜水，泡一泡，简单易做，就可以消除疲劳，还有助于睡眠。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">总结：一年之中只有那么40天的治病黄金时段，错过了，就要等下一年噢，赶紧祛湿养生吧！</p><p><br/></p>', '1', '1', '1', '健康网', '凌寒', '', '1500621948', '1500621948', '2', '', '');
INSERT INTO `news_article` VALUES ('12', '怎样炒青菜好吃又不黄？', '9', '20170721\\4027cd2cb5a9bd4649c6ab02066acc29.jpg', '炒青菜是我们最常吃的家常菜，但是很多时候我们会发现，炒出的青菜很容易颜色变黄，影响食欲。那么怎样炒青菜不变黄呢？其实，要想炒出又绿又嫩的青菜，需要掌握以下几个小技巧。', '青菜，健康', '<p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">炒青菜是我们最常吃的家常菜，但是很多时候我们会发现，炒出的青菜很容易颜色变黄，影响食欲。那么怎样炒青菜不变黄呢？其实，要想炒出又绿又嫩的青菜，需要掌握以下几个小技巧。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　炒青菜好吃的技巧：</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　第一、炒青菜的油量，稍微比平常烹饪多些，没有油份滋润的青菜，也像我们没有各种护肤品滋润的皮肤，没有水盈盈的感觉。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　第二、炒青菜的火候，基本以旺火快炒为主，千万不要用中小火慢慢煮，记得是快炒。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　第三、炒菜时，放盐最关键！有些青菜，比如生菜、大白菜，本身水分已经很足，就一定要在炒到八成熟的时候再放盐，如果早放盐，就会出一滩水，也容易影响菜的颜色；有些青菜，比如通心菜、红薯叶、南瓜苗之类，水分并不多，最好在放油之后，把需要的盐份倒进油里融化掉，再倒青菜入锅内旺火快炒，这样在炒的过程中，会逼出青菜中的一小部分水分，更好地保护此类青菜的翠色。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　第四、有些青菜，在入盘之后千万不要堆起来，要用筷子在盘子中央挑出一处空地，让青菜更快散热，才不会因温度过高使青菜变色。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　炒青菜不变黄的技巧大致就这么多了，希望大家实践的时候再细心观察，总结出更多的技巧。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">第五、还可以青菜的叶和梗分开炒。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p9.pstatp.com/large/2c6100109a6f0c22e075\" img_width=\"552\" img_height=\"352\" style=\"border: 0px; display: block; max-width: 100%; margin-bottom: 10px; margin-top: 10px;\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">1、把青菜洗好；</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2c5f0010a863747be66e\" img_width=\"552\" img_height=\"352\" style=\"border: 0px; display: block; max-width: 100%; margin-bottom: 10px; margin-top: 10px;\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2、把菜籽油熟后，先下菜梗炒；</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2c6f00151b28feef5b9a\" img_width=\"552\" img_height=\"352\" style=\"border: 0px; display: block; max-width: 100%; margin-bottom: 10px; margin-top: 10px;\"/>3、再加菜叶炒；<img src=\"http://p1.pstatp.com/large/2c5f0010a86861ca4e36\" img_width=\"552\" img_height=\"352\" style=\"border: 0px; display: block; max-width: 100%; margin-bottom: 10px; margin-top: 10px;\"/>4、炒软后加盐、糖再炒；</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　酒店炒的青菜，大部分先焯过水，然后再用猛火翻炒入味，所以色泽很漂亮，但是这样操作，极大地造成了青菜中的VC流失，而且在家庭操作中，火候达不到酒店的要求，即使如此操作也不尽人意。不过，只要我们掌握了以上的四大技巧，用家里的火量也能炒出翠绿又脆嫩的一碟加分青菜。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2c6e001096aa2809773d\" img_width=\"552\" img_height=\"352\" style=\"border: 0px; display: block; max-width: 100%; margin-bottom: 10px; margin-top: 10px;\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　温馨提示：</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　1、青菜不要炒得太熟，否则营养会流失。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　2、大火炒会比较香。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　食疗作用：</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　1．提供营养，强身健体青菜为含维生素和矿物质最丰富的蔬菜之一，一个成年人如果每天吃500克青菜，就能满足人体所需的维生素、胡萝卜素、钙、铁等，为保证身体的生理需要提供物质条件，有助于增强机体免疫能力。　　</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　2．保持血管弹性青菜中含有大量粗纤维，其进人人体内与脂肪结合后，可防止血浆胆固醇形成，促使胆固醇代谢物——胆酸得以排出体外，以减少动脉粥样硬化的形成，从而保持血管弹性。　　</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　3．润泽皮肤，延缓衰老青菜中含有大量胡萝卜素（比豆类多1倍，比西红柿、瓜类多4倍）和维生素C，进入人体后，可促进皮肤细胞代谢，防止皮肤粗糙及色素沉着，使皮肤亮洁。延缓衰老。　　</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　4．防癌抗癌青菜中所含的维生素C，在体内会形成一种“透明质酸抑制物”，这种物质具有抗癌作用，可使癌细胞丧失活力。此外，青菜中含有的粗纤维可促进大肠蠕动，增加大肠内毒素的排出，达到防癌抗癌的目的。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　美食禁忌：</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　青菜性偏寒，凡脾胃虚寒、大便溏泄者不宜多食。</p><p><br/></p>', '1', '1', '0', '健康网', '凌寒', '', '1500624371', '1500624371', '2', '', '');
INSERT INTO `news_article` VALUES ('13', '外国人感叹：中国吃货改变全球产业，我们不敢得罪他们', '9', '20170721\\e6fb418aaccb8b2134e0ce61cf746f55.jpg', '吃也能吃出名堂？你想不到中国14亿人的胃口竟然能改变全球的种植业和粮食销售方式。一些国家可羡慕中国有这么多吃货，在2016年中国人仅仅就吃就吃出了一个瑞士一年的GDP！好吓人的数据。', '吃货，美食', '<p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">吃也能吃出名堂？你想不到中国14亿人的胃口竟然能改变全球的种植业和粮食销售方式。一些国家可羡慕中国有这么多吃货，在2016年中国人仅仅就吃就吃出了一个瑞士一年的GDP！好吓人的数据。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ed800002c14275bc09e\" img_width=\"688\" img_height=\"516\" alt=\"外国人感叹：中国吃货改变全球产业，我们不敢得罪他们\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">上图为中国吃货爆满的夜市</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">外国人完全没想到中国这一帮吃货还开创出了一个巨大的商机，为迎合中国人的胃口，一些国家开始绞尽脑汁想办法。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">比如原先瞧不起中国消费市场的智利，在几年前智利的车厘子只有很少一部分销售到中国，但是现在挤破脑袋也要打开中国吃货的大门，智利总统还特意在中国葡萄酒推销会上为他们自己的葡萄酒站台。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2ed70003d850d676e42a\" img_width=\"696\" img_height=\"463\" alt=\"外国人感叹：中国吃货改变全球产业，我们不敢得罪他们\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">上图为智利车厘子出口包装流水线</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">再说德国泛滥的大闸蟹，这是中国吃货最早伤透脑筋的东西。由于德国人本来是不吃大闸蟹，但是听到中国人吃大闸蟹于是就发现了商机，一些渔业公司甚至会选择不远千里将大闸蟹运送到中国等亚洲超市饭店，每公斤还可以买到7-8欧元利润丰厚，现在德国人正在想办法大批量出口到中国来，有求于人啊！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ed70003d84fb5a4719b\" img_width=\"637\" img_height=\"397\" alt=\"外国人感叹：中国吃货改变全球产业，我们不敢得罪他们\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">上图为一个德国人在养殖大闸蟹</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">澳大利亚本来是以采矿业文明于世，但是由于矿产资源属于不可再生资源，很快就枯竭了。可澳大利亚还有一个得天独厚的地理条件适合牛羊的养殖，恰好发现中国大陆这一个庞大到吓人的消费市场，赶紧养牛羊向中国出口吧！目前牛肉出口几乎已经洽谈中，商人表示不敢得罪中国人啊！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ed800002c15e65f1c7b\" img_width=\"690\" img_height=\"459\" alt=\"外国人感叹：中国吃货改变全球产业，我们不敢得罪他们\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">上图为澳大利亚养牛场</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">为了满足中国人的胃口，加拿大人甚至为中国人养起了鸭子！这件事要从1912年说起，那时候的加拿大人是不养鸭子的，但是发现中国这个市场之后，一些加拿大人开始养起了鸭子，一部分出口到中国，另一部分效仿中国餐饮模式，卖给在加拿大拥有庞大数量的华人。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ee200002c20afe5f570\" img_width=\"640\" img_height=\"449\" alt=\"外国人感叹：中国吃货改变全球产业，我们不敢得罪他们\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">上图为加拿大养殖的北京填鸭</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">在西班牙甚至有一个口号“为华人种菜”这又是从何说起？不得不说中国人在世界分布太广了。西班牙一个叫埃尔佩雷约的小镇靠着温润气候种植中国人喜欢吃的蔬菜大获成功，目前在该地拥有为欧洲多国提供近20种中国蔬菜的基地，生菜、西葫芦、西兰花等是销售最好的中国蔬菜。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ee10000db7bbeb57236\" img_width=\"580\" img_height=\"300\" alt=\"外国人感叹：中国吃货改变全球产业，我们不敢得罪他们\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">上图为西班牙妇女采摘种植的小青菜</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">再说说美国，这个世界大国，现在也不得不重视中国吃货的胃口了，目前中国已经超越加拿大成为美国农产品最大出口市场。举个例子在加州因为水荒，水价大涨，但是依然会选择种耗水量高但是中国人非常喜欢的柑橘，因为对加州的这些农场主来说中国人对他们太重要了，直接影响到农场的生死。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p9.pstatp.com/large/2edb00002c14c42b760e\" img_width=\"750\" img_height=\"518\" alt=\"外国人感叹：中国吃货改变全球产业，我们不敢得罪他们\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">上图为美国柑橘种植园</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">在世界经济持续低迷的情况下只有中国一枝独秀，为各国提供了开阔的市场，大把的资金，也难怪他们不敢得罪中国人了。</p><p><br/></p>', '1', '1', '0', '健康网', '凌寒', '', '1500624565', '1500624565', '2', '', '');
INSERT INTO `news_article` VALUES ('14', '入伏之后，切记“七不做”，让你度过健康一整年', '2', '20170721\\87e5605778a4e2bc77ef3d15527bb11d.jpg', '入伏，也就意味着一年中最炎热的盛夏高温季节就此开始。夏秋之交闷热潮湿且多雨，更湿气盛行之时。三伏天不止是养生的好时节，更加是一个需要避病的日子。', '', '<p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">入伏，也就意味着一年中最炎热的盛夏高温季节就此开始。夏秋之交闷热潮湿且多雨，更湿气盛行之时。三伏天不止是养生的好时节，更加是一个需要避病的日子。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ee600014c3cd656b693\" img_width=\"500\" img_height=\"333\" alt=\"入伏之后，切记“七不做”，让你度过健康一整年\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">夏季是很多人最不喜欢的季节，因为不仅热，而且湿气大。但是在中医的眼中，对于人们的健康来说，这是个“冬病夏治”好机会。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">每年三伏酷暑的时候，大家要额外注意，七件事情千万不能做：</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">1</span><span style=\"font-weight: 700;\">、</span><span style=\"font-weight: 700;\">不</span><span style=\"font-weight: 700;\">久坐不动</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">夏天动一动就出汗，久坐会使人供血不足，加重乏力和肌肉酸痛。夏天不可以完全不动，应该要稍微活动一下。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2ee20002cbbd27ded345\" img_width=\"517\" img_height=\"327\" alt=\"入伏之后，切记“七不做”，让你度过健康一整年\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">2</span><span style=\"font-weight: 700;\">、不对着空调</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">一身汗或者运动后大汗淋漓，对着空调使劲吹凉风，湿气会借机顺着张开的汗毛孔进入人体。现在冬天很少见到着凉感冒的人，夏天反而比比皆是，就是因为用空调导致汗液挥发不出来淤积体内。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p9.pstatp.com/large/2ee6000151389f11c408\" img_width=\"366\" img_height=\"220\" alt=\"入伏之后，切记“七不做”，让你度过健康一整年\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">3</span><span style=\"font-weight: 700;\">、熬夜是大忌</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">很多人喜欢熬夜，从中医角度来说，睡眠不足就是脾虚的症状，表明体内湿气过重。每天晚上最好11点前入睡，每天睡够7个小时。适当午睡也是可以的，不过最好别睡过头了，午觉保持在一个小时之内。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2edb0002d4d2555baac4\" img_width=\"500\" img_height=\"323\" alt=\"入伏之后，切记“七不做”，让你度过健康一整年\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">4</span><span style=\"font-weight: 700;\">、</span><span style=\"font-weight: 700;\">要</span><span style=\"font-weight: 700;\">吹干头发</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">夏天时，有的人洗完澡头发湿漉漉的，就坐在空调屋里，甚至头发不吹干就睡觉，导致湿气入侵身体。所以，夏天也要把头发吹干再入睡。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2ed80002cfa6ea2b73bc\" img_width=\"960\" img_height=\"640\" alt=\"入伏之后，切记“七不做”，让你度过健康一整年\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">5</span><span style=\"font-weight: 700;\">、西瓜解暑不宜多吃</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">预防中暑，是大家首先想到的问题。夏季瓜果众多，特别是西瓜，的确能解暑，但不可多食。因其性寒，日常可配些红糖姜茶以平衡其寒性。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ed80002d264a74f888c\" img_width=\"550\" img_height=\"366\" alt=\"入伏之后，切记“七不做”，让你度过健康一整年\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">6、</span><span style=\"font-weight: 700;\">不吃冰饮、雪糕</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">如果怕热，可以喝常温的白开水。如果你能坚持一个三伏天不碰冰饮、雪糕，即使不“冬病夏治”，体内的顽固寒气也能自己好大半。尤其是一到冬天就特别怕冷、手脚冰凉的。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2ee100038675c9c504b9\" img_width=\"400\" img_height=\"297\" alt=\"入伏之后，切记“七不做”，让你度过健康一整年\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">夏天不宜吃生冷的，但是冷粥来上一碗还是可以的。这个冷粥，不是说加了沙冰的那种，而是经过冷藏之后，恢复到室温稍带凉意的粥。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">夏天吃点绿豆也是不错的，有起到解暑的作用。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">绿豆百合薏米粥：</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">材料：绿豆50g 鲜百合30g 薏米100g 冰糖</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">做法：1、将绿豆、薏米洗净，浸泡分2个小时；将百合洗净备用</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2、在开水锅中放入绿豆和薏米，烧开8分钟后改用小火煮至食物开花；</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">3、再加入百合，煮成粥后放入适当的冰糖即可。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ed80002d8819eac36cf\" img_width=\"550\" img_height=\"350\" alt=\"入伏之后，切记“七不做”，让你度过健康一整年\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">7、夏练三伏，但别傻练</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">常说的“夏练三伏”，并不是让大家不分时间、地点、情况地去锻炼。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">医学研究表明，夏天当气温超过35℃、日照超过12小时、湿度高于80％的时候，人体会出汗；且情绪调节中枢就会受到明显影响，人也容易心烦意乱。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">这个时候练一练的方法很简单：端坐、闭目、吞津，就是一个好方法。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">方法：两臂自然下垂，放在腿上，双目和口微闭，调匀呼吸；待口中津液较多时，将其吞咽，可连续吞咽3次；然后，上下牙叩动10～15次，可起到养心安神、固齿健脾的效果。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2ee6000156c60f52586b\" img_width=\"360\" img_height=\"250\" alt=\"入伏之后，切记“七不做”，让你度过健康一整年\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">要做的</span><span style=\"font-weight: 700;\">三件</span><span style=\"font-weight: 700;\">事</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">热水洗澡</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">很多人在进入伏天后，会感到四肢乏力、浑身酸困、汗出过多而黏腻不爽。对付这些的一个好办法，就是洗热水澡。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">进入伏天后，大部分人就会把洗热水澡这个习惯给排除了，觉得夏天要洗冷水才够清凉。其实天气越热，湿邪越重，热水洗澡就显得更有必要，而且洗完以后身体会觉得神清气爽，并不会觉得出汗难受。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ed80002d505512e3bcf\" img_width=\"400\" img_height=\"300\" alt=\"入伏之后，切记“七不做”，让你度过健康一整年\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">吃</span><span style=\"font-weight: 700;\">薏米</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">薏米又名薏苡仁，是常用的中药。又是普遍、常吃的食物，味甘淡微寒,有利水消肿、健脾去湿、舒筋除痹、清热排脓等功效，为常用的利水渗湿药。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">薏米红豆粥：</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">材料：薏米10克、红豆20克</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">做法：将薏米、红豆等洗净，红豆、薏米要提前浸泡</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">将所有材料放入适量的水，文火煲3小时，根据个人口味加入冰糖，即可食用。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">为了更好的效果，可以根据自己的身体情况，添加其它的材料。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ee6000157f7e98f2c39\" img_width=\"540\" img_height=\"346\" alt=\"入伏之后，切记“七不做”，让你度过健康一整年\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">空调</span><span style=\"font-weight: 700;\">温度</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">空调致病的最主要的原因之一，是室内外温差过大。建议23℃~26℃的室内温度比较合适。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p9.pstatp.com/large/2edb0002db67d831abc2\" img_width=\"590\" img_height=\"406\" alt=\"入伏之后，切记“七不做”，让你度过健康一整年\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">总结：这七件事你知道了吗？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">空调的温度你调高了吗？</p><p><br/></p>', '1', '1', '1', '健康网', '凌寒', '', '1500618459', '1500618459', '2', '', '');
INSERT INTO `news_article` VALUES ('15', '蛋白、潜血患者如何避免10~30年不发展成尿毒症？', '12', '20170721\\75961761194cff2942465437a38cca57.jpg', '肾衰竭终末期——尿毒症', '蛋白，潜血患者，尿毒症', '<p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">患者：张xx，男，浙江杭州人，32岁，军人</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">诊断：肾衰竭终末期——尿毒症</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">病史：3岁时感冒发烧，到医院检查蛋白2+、潜血1+，当时诊断为慢性肾炎，患者父母没有意识到肾病的严重性，经治疗后顽固性水肿消除，难治性蛋白尿转阴。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2ecd00024d8f35d852dc\" img_width=\"349\" img_height=\"240\" alt=\"蛋白、潜血患者如何避免10~30年不发展成尿毒症？\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">患者心中有“军人梦”，于是当兵、考军校，在多次严格体检中都未曾发现异常。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">不久之前，参加集训，因劳累过度有头晕、恶心，以及呕吐的症状，晕倒在地，经诊断是尿毒症，所有人非常震惊！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">是什么原因，导致30年之后，这位患者患上了尿毒症呢？</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">这是一名典型的初期只是单纯的蛋白尿、潜血最终发展成尿毒症的患者，作为一名军人，运动量、体力消耗等都比普通人要大一些。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ecf00024b1189ea7e73\" img_width=\"298\" img_height=\"240\" alt=\"蛋白、潜血患者如何避免10~30年不发展成尿毒症？\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">在这种情况下，30年的时间里，患者没有感觉到任何的不适，以至于家属知道检查结果后无论如何也不相信，30年前单纯的蛋白潜血已经恶化成了尿毒症！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">这个例子，也为我们<span style=\"font-weight: 700;\">敲响了警钟</span>：蛋白尿、潜血发展到肾功能不全只是一个时间长短的问题（一般潜伏在10~30年），如果不经过有效的治疗，结果就会发展成尿毒症。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">那么，蛋白尿、潜血患者如何避免发展到10~30年后的尿毒症呢？</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2ecf00024b636d5eed15\" img_width=\"384\" img_height=\"236\" alt=\"蛋白、潜血患者如何避免10~30年不发展成尿毒症？\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">一、&nbsp;<span style=\"font-weight: 700;\">血压、血糖有点高，可别不当回事！</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">肾病患者做好血压监测，是一件及其重要的事情。千万不要因为没症状，就觉得自己血压不高，到了医院一测，血压高的能把自己吓一跳！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">还有的肾病患者血压高还不吃降压药？哎呦喂，看您“作”的！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">降压药不仅降血压，还能有效降低尿蛋白，保护我们的肾功能呢！你确定你不吃？！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">只要遵从医嘱，降压药在人体不会有什么太大的副作用。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">如果是糖尿病患者的不控制好血糖。5—10年可能会出现各种并发症，其中肾衰竭、尿毒症是发病较多较为严重的并发症。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ed6000021febd28519f\" img_width=\"456\" img_height=\"240\" alt=\"蛋白、潜血患者如何避免10~30年不发展成尿毒症？\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">二、&nbsp;<span style=\"font-weight: 700;\">尿液有点异常，要警惕！</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">肾脏很沉默，早期可能一点感觉也没有。尿常规检查就成了，能及时发现肾脏异常的重要手段。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">体检的时候，已经尿蛋白、潜血加号，但却因为不痛不痒，不去肾内科就诊，错过了最佳治疗时机。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">等出现尿少、乏力、恶心这些症状时，采取就诊治疗，或许已经肾衰竭、尿毒症了。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ed2000258a01a89ecf6\" img_width=\"386\" img_height=\"240\" alt=\"蛋白、潜血患者如何避免10~30年不发展成尿毒症？\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">三、&nbsp;<span style=\"font-weight: 700;\">切不可听信“谣言”，乱用偏方！</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">不管中医还是西医，对于慢性肾病来讲，治疗都是一个长期的过程，目的都是为了保护肾功能，维持长期稳定。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">很多肾病患者盲目听信偏方，自以为能治愈能除根，可事实上不但治不了病，还因为某些药物的肾毒性，（比较常见的有：雷公藤、关木通、牵牛子、苍耳子、罂粟壳、草乌、使君子等。其中以雷公藤引起的肾损害最多，其次有关木通及一些中成药制剂），引起了肾脏不可逆转的损害。</p><p><br/></p>', '1', '1', '0', '健康网', '凌寒', '', '1500618533', '1500618533', '2', '', '');
INSERT INTO `news_article` VALUES ('16', '女人多久一次性生活才正常？这个数跟你想象的不一样！', '11', '20170721\\4ad2d73763efa7ee75ff6306516756e4.jpg', '对于女人来说，漂亮是她最强烈的追求。  但女人的卵巢分泌的激素就是女人漂亮的源泉。而规律的合适的性生活则可以更好的养护好女人的卵巢，所以，合适的性生活能让女人更加的漂亮。', '女性，性生活', '<p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">对于女人来说，漂亮是她最强烈的追求。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">但女人的卵巢分泌的激素就是女人漂亮的源泉。而规律的合适的性生活则可以更好的养护好女人的卵巢，所以，合适的性生活能让女人更加的漂亮。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">并且女人卵巢分泌的雌激素，也能让女人的私处更加的水润，这也会增加她的欲望，而且水润的私处对于性生活更加的有好处，体验度更好。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ee10003e8d53356cfd8\" img_width=\"608\" img_height=\"396\" alt=\"女人多久一次性生活才正常？这个数跟你想象的不一样！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">若女人性生活太少，卵巢分泌激素就会变少，时间久了，甚至卵巢可能失去分泌这种激素的能力，还有可能出现早衰现象。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">所以，适当的性生活是很有必要的。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">当然了，若过度的话，也不好，若性生活过度，就会使得盆腔经常处于充血的状态，引起下腹部不舒服，还让得阴道常处于开放的状态，容易引起病菌入侵，引发炎症的产生。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">那么，女人一般多久一次才会更好呢？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">其实，正常的，一般一个周有性生活2次以上的，身体都会更加的健康。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">对于体质比较好的，一周3次以上的，这种女人会显得更加的年轻，拥有更好的气色。</p><p><br/></p>', '1', '1', '1', '健康网', '凌寒', '', '1500618706', '1500618706', '2', '', '');
INSERT INTO `news_article` VALUES ('17', '看舌知健康，老中医教你六个私藏绝学，用了百病全无！', '10', '20170721\\ec855f540e9edb5434a0c2f73f7af077.jpg', '舌头是人体健康最直接的反应之一，老中医想知道你有什么病，看一看就知道了。但对于自己来说舌头出现什么情况代表着哪些健康的信息，我们却一无所知。那么通过舌头我们可以了解到什么信息呢？下面就由我来介绍看舌头知健康的方法吧！', '舌，健康，中医', '<p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">舌头是人体健康最直接的反应之一，老中医想知道你有什么病，看一看就知道了。但对于自己来说舌头出现什么情况代表着哪些健康的信息，我们却一无所知。那么通过舌头我们可以了解到什么信息呢？下面就由我来介绍看舌头知健康的方法吧！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2ed70003a70761c8eef8\" img_width=\"383\" img_height=\"409\" alt=\"看舌知健康，老中医教你六个私藏绝学，用了百病全无！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">【看舌头形状】</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">一、舌象淤血</span><br/><img src=\"http://p3.pstatp.com/large/2ee00000b294b6994d66\" img_width=\"470\" img_height=\"281\" alt=\"看舌知健康，老中医教你六个私藏绝学，用了百病全无！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">1、身体表现：善忘、喉咙发干、性情急躁、皮肤干躁、身体疼痛、面色晦暗、口唇发黑、眼圈暗黑、痛经。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2、原因：血液流动不畅、外伤造成、生气(肝郁气滞就容易造成血瘀)、气虚、寒冷造成。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">3、解决方案：三七与西洋参各0.5克，打粉用温水冲服。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">二、舌象血虚</span><br/><img src=\"http://p1.pstatp.com/large/2ed5000529cca9f35da2\" img_width=\"453\" img_height=\"288\" alt=\"看舌知健康，老中医教你六个私藏绝学，用了百病全无！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">1、身体表现：记忆力差、思考力差、睡眠不好、容易失眠、容易疲惫、怕风怕冷、天热手脚温、天冷手脚冰凉。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2、原因：失血、脾胃功能差不能吸收营养、思虑过多消耗心脾之血。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">3、解决方案：西洋参30克打粉，龙眼肉300克，搅拌均匀隔水蒸4小时，每天一勺用开水冲服。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">三、舌象阴虚</span><br/><img src=\"http://p1.pstatp.com/large/2ed90000cf18ff8a0787\" img_width=\"328\" img_height=\"290\" alt=\"看舌知健康，老中医教你六个私藏绝学，用了百病全无！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">1、身体表现：眼干、口躁咽干、手足心发热、大便干躁、眩晕耳鸣、睡眠差、脉搏快、喜冷饮、心烦爱发火、腰膝酸软。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2、原因：津液不足、熬夜、吃辛辣及吃过多热性的食物、生大病。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">3、解决方案：生地、沙参、麦冬、枸杞各9克，石斛6克、当归3克、猪龙骨一节，煲汤服用，一周即可。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">四、舌象阳虚</span><br/><img src=\"http://p1.pstatp.com/large/2ee00000b2febe17be29\" img_width=\"271\" img_height=\"285\" alt=\"看舌知健康，老中医教你六个私藏绝学，用了百病全无！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">1、身体表现：怕冷、喜饮热饮、精神不振、睡眠偏多、口唇色淡、毛发易落、易出汗、大便稀薄、小便清长。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2、原因：过多吃寒凉的药、喝冷饮、体质弱、过份暴露在寒冷的环境里。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">3、解决方案：当归20克、生姜30克，羊肉500克，煲汤服用。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">五、舌象肝气不疏</span><br/><img src=\"http://p1.pstatp.com/large/2ed70003a7eac4a0af1e\" img_width=\"280\" img_height=\"267\" alt=\"看舌知健康，老中医教你六个私藏绝学，用了百病全无！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">1、身体表现：神情抑郁、口苦口干、眩晕、胃口不佳、忽冷忽热、心烦火大、呕逆、胸闷肋疼、失眠多梦。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2、原因：思虑重、压力大(七情内伤：喜则气散、怒则气上、优则气聚、思则气结、悲则气消、恐则气下、惊则气乱)。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">3、解决方案：灯芯草、竹叶各3克，泡水喝，3-5天即可，主要用于突然上火需要疏肝理气。长期肝气不疏主要需要调整“喜怒优思悲恐惊”七情。慈悲心是克制七情最有力的法宝，即仁者寿。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">六、<span style=\"font-weight: 700;\">舌象水湿重</span><br/><img src=\"http://p3.pstatp.com/large/2ee00000b368fa3e6992\" img_width=\"375\" img_height=\"290\" alt=\"看舌知健康，老中医教你六个私藏绝学，用了百病全无！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">1、身体表现：面部(眼皮)浮肿、油光、易生痤疮、腹泻、身重困倦、大便燥结、急躁易怒。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2、原因：气虚、运动少、饮冷饮，造成水湿留在体内。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">3、解决方案：红豆、薏仁各10克，煮水30分钟，代茶饮，祛署湿，适合夏天。冬天寒湿则适应用绿色的花椒，煮水泡脚。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">【看舌苔颜色】</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">一、</span><span style=\"font-weight: 700;\">健康的舌苔</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">呈现淡粉颜色，表面清爽干，不肥厚。口腔内清洁无异味。舌苔本身光润，在其上表面上，没有牙齿咬合的压印。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">二、</span><span style=\"font-weight: 700;\">“上火”的舌苔</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">舌苔上表面的颜色发白。这层白是薄薄的一层，像是长在舌头上面一样。偶尔出现这种情况到没什么关系，如果连续几天，则是身体“上火”了，多喝开水，有几天就可自行消除。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">三、</span><span style=\"font-weight: 700;\">肠胃出问题的舌苔</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">舌苔上表面厚重，上面像糊了一层东西，且颜色发白。此时，如果用干净的手指甲去刮舌苔表面，能刮出这层类似残留物的东西。此现象表征体内的肠胃，尤其是胃部不舒服。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">四、</span><span style=\"font-weight: 700;\">胃寒的舌苔</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">舌苔上表面浑浊。一般原因涉及：胃寒、思想情绪过度紧张、睡眠不好等导致。常伴有口臭、口苦的症状。用指甲刮东西，效果同上。与人面对面交谈时候，得借助口香糖的掩饰。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">五、</span><span style=\"font-weight: 700;\">问题大的舌苔</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">舌苔上表面发黑。这种颜色对应的身体大多是慢性疾病，如果发现有发黑的颜色了，尽快去做个全面检查。因为其对应的是慢性疾病，多半是肾脏器官出现问题，要重视啊。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">总结：你舌像是那种？你的舌苔是什么颜色？</p><p><br/></p>', '1', '1', '0', '健康网', '凌寒', '', '1500618838', '1500618838', '2', '', '');
INSERT INTO `news_article` VALUES ('18', '狂犬病发作是一种什么感觉？', '9', '20170721\\2cd9d4b7938f91d5071338a826863a92.jpg', '狂犬病是因为狂犬病毒导致的一种急性的传染病可以在人或者是动物身上都能发病，狂犬病最容易在狗狼猫等肉食动物身上存在，人得狂犬病多半是因为被动物咬伤感染而导致的。当人得了狂犬病之后非常容易怕水怕风，咽喉会出现痉挛的现象，甚至可以导致患者瘫痪。由于狂犬病具有很强的恐水特征，所以，又可以把狂犬病叫做恐水症。当人得了狂犬病之后中枢神经系统会受到很大的危害', '狂犬病，感觉', '<h1 style=\"margin: 0.5em 0px; padding: 0px 0px 0px 12px; font-size: 18px; line-height: 24px; position: relative;\">狂犬病发作可能会有各种妄想</h1><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"font-weight: 700;\">狂犬病是因为狂犬病毒导致的一种急性的传染病可以在人或者是动物身上都能发病，狂犬病最容易在狗狼猫等肉食动物身上存在，人得狂犬病多半是因为被动物咬伤感染而导致的。当人得了狂犬病之后非常容易怕水怕风，咽喉会出现痉挛的现象，甚至可以导致患者瘫痪。由于狂犬病具有很强的恐水特征，所以，又可以把狂犬病叫做恐水症。当人得了狂犬病之后中枢神经系统会受到很大的危害。</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">1狂犬病这种疾病具有潜伏期，一般潜伏期为一到三个月，当患者处于潜伏期的时候，不会发生症状，但是有的狂犬病潜伏期会非常短，一般七天左右，最长的狂犬病潜伏期可以达到一年以上。狂犬病过了潜伏期之后就开始发病。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><img src=\"http://p1.pstatp.com/large/2c6800113af05ecf4536\" img_width=\"386\" img_height=\"300\" style=\"border: 0px; display: block; max-width: 100%; margin-bottom: 10px; margin-top: 10px;\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">2当狂犬病开始发作的时候患者会出现全身不舒服的感觉，还会高烧不退，身体会觉得非常疲倦，全身都很乏力，心里有不安的感觉还会出现恶心等症状，狂犬病患者对于疼痛声音光线等等外界的刺激往往都具有很强的过敏反应。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">3当狂犬病发作之后患者的咽喉会出现一些症状，比如说，患者的咽喉会出现紧缩感，这可能是因为狂犬病的病毒在患者的伤口附近大量繁殖然后刺激了患者的神经系统而引起的。当狂犬病病毒刺激患者的神经系统之后患者会出现麻木发痒刺痛的感觉。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"font-weight: 700;\">注意事项：</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">当狂犬病的症状变得强烈之后，就会非常明显，患者的身体会出现严重的痉挛，这个时候，患者就会开始精神错乱，会出现一些幻觉，比如说患者可能出现一些妄想的现象，还可能出现幻听怕光怕声音的症状。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">　</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><br/></p><p><a href=\"http://www.wukong.com/answer/6444859317500772622/?showComment=6444859317500772622\" target=\"_blank\" class=\"show-comment\" style=\"cursor: pointer; text-decoration-line: none; color: rgb(153, 153, 153); float: left; line-height: 16px; margin-left: 0px;\"><span class=\"y-icon icon-comments_details\" style=\"position: relative; display: inline-block; line-height: 1; font-size: 12px; color: rgb(202, 202, 202); -webkit-font-smoothing: antialiased; font-family: yFont !important;\"></span>&nbsp;16评论</a></p><p><a href=\"http://www.wukong.com/user/?uid=6209942958\" class=\"answer-user-avatar\" target=\"_blank\" style=\"cursor: pointer; text-decoration-line: none; color: rgb(153, 153, 153); width: 40px; height: 40px; border-radius: 50%; display: inline-block; position: relative; overflow: hidden; margin-right: 8px; float: left;\"><img class=\"\" src=\"http://p3.pstatp.com/thumb/289c00176c1193cac97e\" alt=\"\" style=\"border: 0px; width: 40px; height: 40px;\"/></a><a class=\"answer-user-name\" href=\"http://www.wukong.com/user/?uid=6209942958\" target=\"_blank\" style=\"cursor: pointer; text-decoration-line: none; color: rgb(34, 34, 34); display: block; font-size: 14px; line-height: 20px; margin-top: 2px; white-space: nowrap; width: 490px; text-overflow: ellipsis; overflow: hidden; margin-bottom: 2px;\">谢文文82375033</a><a class=\"answer-user-tag\" style=\"cursor: pointer; color: rgb(153, 153, 153); display: block; margin-top: 2px; width: 620px; margin-bottom: 2px;\">5小时前</a></p><p><span class=\"y-icon icon-digg\" style=\"position: relative; display: inline-block; line-height: 1; color: rgb(202, 202, 202); -webkit-font-smoothing: antialiased; margin-right: 4px; font-family: yFont !important;\"></span><span data-node=\"like-count\">17</span>赞</p><p><span class=\"y-icon icon-thumbsdown_normal\" style=\"position: relative; display: inline-block; line-height: 1; color: rgb(202, 202, 202); -webkit-font-smoothing: antialiased; margin-right: 4px; font-family: yFont !important;\"></span>踩</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"line-height: 28px;\"><img src=\"http://p3.pstatp.com/large/2c610011bb69ca9704b5\" img_width=\"622\" img_height=\"345\" style=\"border: 0px; display: block; max-width: 100%; margin-bottom: 10px;\"/><br/><br/></span></p><p><span style=\"line-height: 28px;\">人被致病或带毒的动物咬伤抓伤后，狂犬病毒侵入破损的伤口，经一断潜伏期后沿着神经纤维逆行到中枢神经而发病。 潜伏期：短则三天，长者二十几年，常见的是10—100天。</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"line-height: 28px;\">这与个体抵抗力、咬伤程度、部位及动物带毒量有关。一般说来，咬得越重，咬伤部位越靠近头部或咬伤部位神经越丰富，则发病越快。 临床上病程分为三期，即前驱期、兴奋期和瘫痪期，但总病程不超过一周。而且目前尚无特效药治疗，也就是说，一旦发病死亡率100%。</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"line-height: 28px;\">前驱期：头痛、低热、食欲不振、倦怠、烦躁、恐惧不安，对声、光、风等刺激敏感并有窒息感。已愈合的伤口周围感觉异常，如发痒、刺痛、麻木、蚁行感。 兴奋期：极度恐惧，怕水、怕风、怕声、怕光。尤其是极端的恐水，甚至听到水的声音都会全身抽搐，所以狂犬病也叫恐水症。这是因为病人交感神经亢奋，喉头肌肉已痉挛，听到水声条件反射喝水而诱发全身神经兴奋、肌肉紧张。</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"line-height: 28px;\"><img src=\"http://p3.pstatp.com/large/2c6e0011b9b5bb4134ac\" img_width=\"500\" img_height=\"300\" style=\"border: 0px; display: block; max-width: 100%; margin-bottom: 10px;\"/><br/><br/></span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"line-height: 28px;\">本期还表现心率加快，大汗淋漓，唾液大量分泌又不能咽下而从嘴角流出，喉舌肌肉痉挛痛苦发出呻吟声，有时象狗的呻叫声，再加之手指疼痛痉挛乱拍乱抓，所以传说狂犬病人象狗一样。 瘫痪期：体力消耗怠尽，痉挛停止，全身瘫软。最终因呼吸肌麻痹，循环衰竭而死亡。</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"line-height: 28px;\">狂犬病是一种可怕的疾病,据媒体报道,最近一名8岁女孩狂犬病发作身亡。下面一起去了解一下狂犬病。 一个8岁的女孩,在半年前被狗咬伤因为没有及时治疗打狂犬疫苗而死亡,让很多人为之惋惜。现在很多的家庭都在养狗,这种宠物可以给人们的生活增添乐趣,但是,城市的狗都会经过防疫部门的检疫,而一些比较落后的乡镇是很难做到全面检疫的,所以,很多的狗并不健康,而且,狗有一种最凶险的疾病就是狂犬症,这是因为狂犬病毒特别容易侵犯狗而引起的,患上狂犬症的病狗一旦咬伤人,人就会感染狂犬病毒患上狂犬症,</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"line-height: 28px;\">但是狂犬症有一个潜伏期,可能当时不发病,但是如果不进行治疗,一定会患上狂犬病。而狂犬病的死亡率非常高,很难挽救。 那什么是狂犬病?会有哪些临床表现?我们一起来看一下。 狂犬病是由狂犬病毒所致的急性传染病,可以人兽共患,主要发生在一些食肉的动物,比如狗、狼、猫等,而最常见的就是狗和猫,狼等动物在日常生活中是很难接触到的,而狗和猫已经成为人们喜欢豢养的宠物,所以,狗和猫成为狂犬病的主要传染者,而人多因被病狗或者是病猫咬伤而感染。</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"line-height: 28px;\"><img src=\"http://p1.pstatp.com/large/2c610011bb74a59779b4\" img_width=\"640\" img_height=\"497\" style=\"border: 0px; display: block; max-width: 100%; margin-bottom: 10px;\"/><br/><br/></span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"line-height: 28px;\">因此,现在预防狂犬症最好的办法就是确定被咬伤以后立即打狂犬疫苗,只要是打狂犬疫苗可以90%控制狂犬病的发生。 那狂犬病的表现症状?</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"line-height: 28px;\">1、狂犬病发作之前往往有一个短暂的前驱期,这个时间会持续1至4天,主要的症状是表现为中度发热,全身感觉不适,食欲下降甚至消失,同时还伴有头痛、恶心等。症状类似感冒,因此,很容易被忽略。</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"line-height: 28px;\">2、过了前驱期之后就进入神经系统的症状期,这个症状会出现在2天至20天,主要的表现是应激性增高,感觉胸部有很强的压迫感,同时伴有胸痛及气流恐怖症,严重的当风吹面部时都会引起咽喉部肌肉的痉挛,进而影响呼吸和进食,这是狂犬病非常典型的症状之一。对临床判断有利。</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"line-height: 28px;\">3、局部症状。被病狗咬伤的伤口部位会有疼痛,还可能出现有麻木、痒痛的异常感觉,手脚四肢仿佛有蚂蚁在爬,非常痛苦。还有的病人同时伴有对各种光、噪音和感觉剌激的应激性增高,不愿意见光,也不愿意被触碰,而一旦受刺激,脸部的表情会非常的恐惧和恐怖,这是因为病毒侵犯神经表现出肌张力增高和面部肌肉痉挛所致。</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"line-height: 28px;\">4、交感神经系统病受损的症状。当交感神经受到损后,病人会出现多汗、流涎、狂躁等表现,而且表现出焦虑、痉挛性痛性肌肉收缩,而且大多数人在吞咽时引起咽喉等部位的肌肉痉挛所以很怕喝水,故又称恐水症。 狂犬病的死亡率非常高,所以千万不能掉以轻心,如果不慎被狗或者猫咬伤,就要立即去医院进行注射狂犬疫苗,千万不能像本文中的8岁的小女孩一样,白白的送上了性命。</span></p><p><a href=\"http://www.wukong.com/answer/6445019792632971533/?showComment=6445019792632971533\" target=\"_blank\" class=\"show-comment\" style=\"cursor: pointer; text-decoration-line: none; color: rgb(153, 153, 153); float: left; line-height: 16px; margin-left: 0px;\"><span class=\"y-icon icon-comments_details\" style=\"position: relative; display: inline-block; line-height: 1; font-size: 12px; color: rgb(202, 202, 202); -webkit-font-smoothing: antialiased; font-family: yFont !important;\"></span>&nbsp;10评论</a></p><p><a href=\"http://www.wukong.com/user/?uid=24629342802\" class=\"answer-user-avatar\" target=\"_blank\" style=\"cursor: pointer; text-decoration-line: none; color: rgb(153, 153, 153); width: 40px; height: 40px; border-radius: 50%; display: inline-block; position: relative; overflow: hidden; margin-right: 8px; float: left;\"><img class=\"\" src=\"http://p3.pstatp.com/thumb/2498000be9f82ea18e36\" alt=\"\" style=\"border: 0px; width: 40px; height: 40px;\"/></a><a class=\"answer-user-name\" href=\"http://www.wukong.com/user/?uid=24629342802\" target=\"_blank\" style=\"cursor: pointer; text-decoration-line: none; color: rgb(34, 34, 34); display: block; font-size: 14px; line-height: 20px; margin-top: 2px; white-space: nowrap; width: 490px; text-overflow: ellipsis; overflow: hidden; margin-bottom: 2px;\">萌萌萌萌猛嘉</a><a class=\"answer-user-tag\" style=\"cursor: pointer; color: rgb(153, 153, 153); display: block; margin-top: 2px; width: 620px; margin-bottom: 2px;\">07-20 20:16</a></p><p><span class=\"y-icon icon-digg\" style=\"position: relative; display: inline-block; line-height: 1; color: rgb(202, 202, 202); -webkit-font-smoothing: antialiased; margin-right: 4px; font-family: yFont !important;\"></span><span data-node=\"like-count\">345</span>赞</p><p><span class=\"y-icon icon-thumbsdown_normal\" style=\"position: relative; display: inline-block; line-height: 1; color: rgb(202, 202, 202); -webkit-font-smoothing: antialiased; margin-right: 4px; font-family: yFont !important;\"></span>踩</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">狂犬病发作表现一：性欲高涨频繁射精</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">　　北京一名32岁的男子，一天内出现频繁射精的现象，多达40—50次后，最终因抢救无效身亡。据悉，该男子并不是“精尽人亡”，检测发现其狂犬病病毒呈阳性，原来是死于狂犬病发作。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">　　专家表示，狂犬病导致患者出现性功能异常，可能是因为大脑结构受损有关。根据尸检报告，推测持续勃起和射精或许是狂犬病毒损伤了大脑结构引发的。此外，还有人认为，异常勃起和射精，可能是因为狂犬病毒入侵了脊髓腰骶段，产生了刺激性病灶，不断施放冲动使泌尿生殖系统工作异常。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">　<span style=\"font-weight: 700;\">　狂犬病发作表现二：一听到水就害怕</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">　　李大爷本是一家工厂的看门人，事发前，他常喂食厂门外的一条流浪狗。有一天李大爷被这只狗咬伤也没有在意，他只是在家简单地清洗了伤口。大约两个月后，他向家人提及此事时，已经出现了狂犬病的症状。他虽然口很干却喝不下水，只要有人提及“水”字他就会害怕，老人临去世时麻药已经对他不起作用了。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">　　狂犬病毒具有嗜神经性，病毒自中枢神经系统向周围神经离心性扩散侵入各组织与器官，其中尤以唾液神经核舌咽神经核和舌下神经核受损，临床上可出现恐水、呼吸困难、吞咽困难等症状。狂犬病患者就算是口渴，也会因为喉咙痉挛而喝不了，甚至想到水就害怕。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">　　<span style=\"font-weight: 700;\">狂犬病发作表现三：风一吹就发抖</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">　　金阿姨和丈夫在家吃饭，突然一只流浪狗跑了进来，金阿姨上前挥赶，谁知小狗竟一口咬伤阿姨的手，想着就流了<span style=\"line-height: 28px; text-decoration-line: underline;\">一点血</span>，就没去医院直接将<span style=\"line-height: 28px; text-decoration-line: underline;\">创可贴</span>，以为事情就过去了，但谁也没想到这才是噩梦的开始。大约过2个月后，在农田里干活的金阿姨突然觉得有些胸闷，偶尔有风吹来就愈发不适。经过几天的折磨，金阿姨由于呼吸系统衰竭，她离开了人世。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">　　因为神经受损，患者可出现相应神经支配的功能异常症状，比如吞咽及呼吸困难、怕光怕水怕风、唾液分泌增多及情绪波动、高热、头痛、<span style=\"line-height: 28px; text-decoration-line: underline;\">呕吐</span>、腱反射消失、<span style=\"line-height: 28px; text-decoration-line: underline;\">瘫痪</span>等神经系统症状。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\"><span style=\"font-weight: 700;\">　　狂犬病发作表现四：潜伏期可长可短</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">　　28岁的男子张伟平时身体一直很好，膝下有一双女儿，日子过得安稳惬意，但在今年6月4日，他突然狂躁不安，在家里见什么摔什么，家人于是将其带至淮阴县医院进行救治，病情不见好转又转至淮安市第四人民医院，后被医院诊断为狂犬病，直到此时，家人才回想起，张伟在其5岁时确实被狗咬过，但没想到这个病竟潜伏在其体内23年之久，6月6日晚11时许，潜伏在其体内23年的狂犬病病毒最终要了张伟的性命。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">　　狂犬病潜伏期可长可短，10天至1年都有，甚至又长达20年之久，但发病时却是来势汹汹。狂犬病发病后病程特别短，从出现症状到死亡仅仅6天左右，最长不超过10天左右。这是因为，病毒进入大脑完全破坏神经仅仅需要24小时，没有任何药剂可以在几天内不损害中枢神经系统的方式<span style=\"line-height: 28px; text-decoration-line: underline;\">下消</span>灭病毒并<span style=\"line-height: 28px; text-decoration-line: underline;\">修补</span>神经。这就是为什么狂犬病患者一旦发病几乎100%死亡的原因，但狂犬病的潜伏期则可长可短。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word;\">以上就是狂犬病发作的几种奇葩表现，想要远离狂犬病，平时就要少接触患病的犬类或猫科动物，一旦被咬及时打狂犬疫苗，以免后患。</p><p><br/></p>', '1', '1', '0', '健康网', '凌寒', '', '1500618940', '1500618940', '2', '', '');
INSERT INTO `news_article` VALUES ('19', '降压药加每天1万步3年，为何血压降不好？', '8', '20170721\\4f299a450f77fb2f837a26d0d483cb2f.jpg', '余千兰：你能告诉我，你年龄、没服药时最高血压多少？体检结果？以前患过什么疾病？都服过哪些药？身高、体重？', '降压药，运动', '<p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">网友：医生、你好！我一直吃氨氯地平贝那普利片，每天一片，效果一般，怎么办？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">余千兰：你能告诉我，你年龄、没服药时最高血压多少？体检结果？以前患过什么疾病？都服过哪些药？身高、体重？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ecd000367ede1381e2e\" img_width=\"1280\" img_height=\"851\" alt=\"降压药加每天1万步3年，为何血压降不好，运动不能降血压吗？你别不相信\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">网友：身高183，体重89kg，没有得过什么病，体检结果很好，服降压药前背了一天动态血压，就开始吃降压药了。早起后测量血压一般在149/96左右。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">余千兰：你体重指数26.6（体重公斤/身高平方米），正常人小于24，你有点偏胖，建议减肥有利于血压下降。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">你是什么工作？现在血压偏高，抽烟喝酒吗？生活规律吗？，检查过同型半胱胺酸的检测吗？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">网友：就是抽烟！没有做这个检测，晚上睡得晚！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">余千兰：好的，你目前血压药配得可以，只要戒烟，加强运动，减肥，少熬夜，这样降压效果可以增强的。可以检查下血同型半胱胺酸。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ed700013d5346b4adc9\" img_width=\"1280\" img_height=\"853\" alt=\"降压药加每天1万步3年，为何血压降不好，运动不能降血压吗？你别不相信\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">网友：每天我走一万多步，已坚持3年了！每天一片量够吗？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">余千兰：可以的，服药时注意测量血压。运动降压只是一方面 ，运动可以使血压降低大约4~9mmHg。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">如果说影响你的血压不光是运动，有时效果不佳，观察影响血压的原因，饮食习惯？高钠低钾（重口味）？体重？熬夜？心情？没有戒烟？等等，有的人抽烟可能不影响血压或血压不高，但是针对个体，你就是不该抽烟，一抽就影响血压，谁让遗传基因抵抗不了呢？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">网友：医生，这个类型药每天一片量是不是少了一点？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">余千兰：不少，是联合用药，故比单用要少。关键是服后血压情况，观察。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">网友：测量血压还是早餐前测量吗？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ed50002c148b75039b8\" img_width=\"1280\" img_height=\"755\" alt=\"降压药加每天1万步3年，为何血压降不好，运动不能降血压吗？你别不相信\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">余千兰：若血压不稳定，早晚都要测量，若稳定，早餐前测量。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">网友：医生，测后再服药 对吗？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">余千兰：可以。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">上面的网友不能改善生活方式，可能要加服药， 但不是加量，是加利尿剂（没有禁忌证），这样不是最好的办法。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">高血压病友在管理自己的高血压时，到底什么 时候测血压好？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">1，每天睡醒时即测量血压。此时的血压水平能反映药物降压作用的持续效果、夜间睡眠时的血压情况。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2，高血压病友，在刚开始服降压药时，或换服药时，应该每隔数小时测量1次，或进行24小时动态血压监测，以确定换药的方案是否适合自己。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ed50002c1b6bb1db8c8\" img_width=\"1280\" img_height=\"1273\" alt=\"降压药加每天1万步3年，为何血压降不好，运动不能降血压吗？你别不相信\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">3，服降压药后2~6小时测量血压。因为降压药作用高峰分别在服药后2~6小时出现，此时测量血压可以反映药物的最大降压效果。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">4，大多数人的血压明显呈昼夜节律性，即白天血压较高，夜间睡觉血压较低，呈双峰一谷现象，即上午6~10时及下午4~8时，在这两个时段测量血压，可以了解1天中血压的最高点，防心脑血管意外的发生。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">5，定时服降压药，不要随意减量或停药，若有必要，建议在医生指导下评估病情调整。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">6，若血压稳定，可以定期测量血压，1~2周可以测量1次。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">7，最好自备血压计并学会自测血压，不要为测血量压而焦虑，感觉不适时提醒自己测量血压。多和你的医生交流，让你的诊疗方案能控制好血压到目标值（＜140/90mmHg）以下，才能真正保护好全身血管！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2ed50002c26140888505\" img_width=\"1280\" img_height=\"853\" alt=\"降压药加每天1万步3年，为何血压降不好，运动不能降血压吗？你别不相信\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">真的，高血压治疗就是：心态+生活不良方式改善+坚持服降压药，心态和改善不良生活方式是降高血压的基石，降压药物是治疗高血压的保障！</p><p><br/></p>', '1', '1', '0', '健康网', '凌寒', '', '1500619057', '1500619057', '2', '', '');
INSERT INTO `news_article` VALUES ('20', '远离性侵，务必让你的孩子知道“懂得、明确、不准”', '7', '20170721\\09a137181dee4f04b71109cfba4b61f9.jpg', '性教育，是家庭教育重要的分支，是家长和孩子和谐相处，无障碍沟通交流的重要内容，也是家长智慧教子的美好体验。', '性侵，三懂得，三明确，三不准', '<p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ee50000e4e2b0574b84\" img_width=\"1280\" img_height=\"881\" alt=\"远离性侵，务必让你的孩子知道“三懂得”、“三明确”、“三不准”！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><hr style=\"margin: 40px auto; padding: 0px; width: 64px; height: 4px; background: rgb(237, 64, 64); outline: 0px; border: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal;\"/><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">最近网上看到一则消息“老师又来盖被子了”。报道的是广西平南县思旺镇，某托管机构的多名女童近日指认，一名中学男教师谭某，利用兼职查宿机会，长期对她们作出“不雅举动”。<br/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">根据受访女童的反映，该举动至少包括猥亵，最早的一次可追溯到4年前。事发后，谭某被刑拘接受进一步调查。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">另一则消息，南京一位母亲将10岁女儿放在书店自己外出，一会回来正看见一个70岁的老人对玩电脑的女儿动手动脚。母亲愤而报警，不规矩的老人被拘留。事后问女儿知不知道那人在对你做坏事，女儿懵懵懂懂地说：“感觉到了，可是不知他在做什么。”</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">侵犯在发生，我们的女儿却毫不知觉，或者有所知觉却放纵容忍！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">这两个案件反映了对孩子性安全教育的严重缺失。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">由于家长不懂得不屑于或者羞于对孩子进行性安全教育，导致孩子对于自己的身体缺乏权利意识，对性侵的概念模糊不清，对如何远离性侵，应对性侵缺乏足够的常识和</span><span style=\"font-weight: 700;\">自我保护意识</span><span style=\"font-weight: 700;\">，使得坏人的侵害屡屡得逞。</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">韩国电影《素媛》，讲述了一个8岁女孩在遭遇性侵后，如何在家人帮助下，走出心灵阴影面对生活的故事，这是根据韩国一个真实事件改编而成。<br/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">一个雨夜上学途中，素媛遇到一个醉酒大叔，以“合伞”为由，将素媛骗至偏僻小屋，实施了毫无人性的侵犯。备受摧残的素媛被装上“人工肛门”，父亲帮助她清理排便时，她惊恐万状、歇斯底里，所有男性信息特征都会令她恐惧、愤怒和排斥。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">影片中素媛在爱里得到新生，而现实中，故事原型的遭遇比素媛还要悲惨。创伤不仅仅摧毁了她的肉体，更造成女孩一生走不出心理阴影。对自己的厌恶，对人性的怀疑，对这个世界的冷漠与憎恨。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ee60000db6a223f7a13\" img_width=\"765\" img_height=\"510\" alt=\"远离性侵，务必让你的孩子知道“三懂得”、“三明确”、“三不准”！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">一个渣滓的偶尔冲动，制造了一起刑事案件，却造成了一个家庭的崩溃和一个女孩一生幸福的毁灭。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">家长千防万防，总有顾及不到的时候。教会孩子及时识别，断然规避和巧妙应对，才是保护孩子的唯一正确途径。</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">良好愿望+基本常识灌输+具体细节演练=护孩子一世周全</span><span style=\"font-weight: 700;\">。</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">问卷调查显示，家长对孩子的性安全教育，存在着三个顾虑，即：不知何时说，不知说到什么程度，唯恐诲淫诲盗。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">其实，这些担心很没必要。对孩子的性教育，应该跟孩子饮食安全、健康教育同时进行。比如，一个妈妈坚持不给自己的孩子穿开裆裤，她对孩子说，排大小便的地方属于隐私，不能裸露在外面。自然而然就把隐私概念引出来了。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">再比如，男孩子三周岁以后，妈妈上厕所换衣服时要关门，并规定孩子进门需要敲门征得同意后方可进入，这就引出了男女有别的意识。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">性教育尺度上，不必用成年人眼光看待 。对于孩子来说，性器官跟人的眼睛鼻子一样只是个器官，大大方方讲出名称和功用，孩子就像其他器官一样平静接受。相反，扭扭捏捏躲躲闪闪，才会引发孩子的好奇。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">对孩子的性教育越早越好。在孩子纯洁如白纸时，在日程生活点点滴滴，在看似无意识的点化中，自然而然，润物无声地将性安全知识灌输给孩子。当孩子遇到坏人欲行不轨时，会立刻警醒，采取必要的防护措施，避免侵害发生。</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2edb00025f13b192a85a\" img_width=\"1280\" img_height=\"1036\" alt=\"远离性侵，务必让你的孩子知道“三懂得”、“三明确”、“三不准”！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">如何对孩子进行性安全教育。本人总结概括为“三懂得”、“三明确”、“三不可以”。</p><h1 style=\"margin: 36px 0px 22px; padding: 0px; font-size: 24px; line-height: 32px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">“三懂得”</h1><ol class=\" list-paddingleft-2\" style=\"padding-top: 20px; padding-right: 30px; padding-bottom: 20px; list-style-position: outside; list-style-image: initial; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><li><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\">懂得身体是自己的，别人无权触碰；</p></li><li><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\">懂得成年人触碰自己背心裤衩覆盖的地方，是不道德行为；</p></li><li><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\">懂得反抗成年人对身体的侵犯不算没礼貌，为保护自己说谎不算犯错误。</p></li></ol><h1 style=\"margin: 36px 0px 22px; padding: 0px; font-size: 24px; line-height: 32px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">“三明确”</h1><ol class=\" list-paddingleft-2\" style=\"padding-top: 20px; padding-right: 30px; padding-bottom: 20px; list-style-position: outside; list-style-image: initial; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><li><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\">明确性侵和猥亵的部位和尺度（孩子没有成人那样敏感龌龊），让孩子区分喜爱与侵犯的不同；</p></li><li><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\">明确男女大防，异性长辈手足，在穿衣便溺居住方面有所避讳；</p></li><li><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\">明确父母永远是你的靠山。无论闯了多大的祸，记得跟父母求助，不受坏人要挟，做自己不喜欢的事。</p></li></ol><h1 style=\"margin: 36px 0px 22px; padding: 0px; font-size: 24px; line-height: 32px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">“三不能”</h1><ol class=\" list-paddingleft-2\" style=\"padding-top: 20px; padding-right: 30px; padding-bottom: 20px; list-style-position: outside; list-style-image: initial; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><li><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\">女孩子外出，不能走单，有权利要求成年人陪送；</p></li><li><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\">女孩与异性不能单独长时间共处一室，一车;</p></li><li><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\">女孩子面对陌生人的求助，搭讪，坚决说不，有事找警察，不做烂好人。</p></li></ol><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">“三懂得”强调的是权利，“三明确”强调的是界限，“三不能”强调的是行为。三方有机结合，形成孩子高度敏感高度警觉的思维和行为模式。在百毒不侵，无孔可入的孩子面前，坏人也会怂的。</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">性教育，是家庭教育重要的分支，是家长和孩子和谐相处，无障碍沟通交流的重要内容，也是家长智慧教子的美好体验。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">任何担忧和顾虑都是多余的。</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">任何探索和实践都值得点赞。</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">因为，保护孩子，就是保护我们的安详生活和美好未来</span>。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p9.pstatp.com/large/2ee200025edde036c2c1\" img_width=\"1280\" img_height=\"1280\" alt=\"远离性侵，务必让你的孩子知道“三懂得”、“三明确”、“三不准”！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p><br/></p>', '1', '1', '0', '健康网', '凌寒', '', '1500619233', '1500619233', '2', '', '');
INSERT INTO `news_article` VALUES ('21', '产检被医生1个问题羞红了老脸，真好尴尬啊', '6', '20170721\\45106c08ce84075aad7d446061256eff.jpg', '产前检查顾名思义为生产前的检查，又称围产保健，能及时了解孕妇身体情况及胎儿的生长发育情况，保障母亲和胎儿的健康和安全，做到优生优育。', '产检，女性', '<p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">产前检查顾名思义为生产前的检查，又称围产保健，能及时了解孕妇身体情况及胎儿的生长发育情况，保障母亲和胎儿的健康和安全，做到优生优育。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">常规检查：测量血压和体重，是每次产前检查的保留项目;而首次检查这两项的结果，有助于医生掌握准妈妈的健康状况，如果以后某次检查孕妇的血压或体重上升得比较快，医生就会有所警觉，并采取相应的方法解决。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">为了生个健康的宝宝，孕期各种各样的产检我们是逃不掉的，产检的次数多了，难免会遇到让人尴尬的事。例如：医生问，以前有流产史吗？这时候如果没有还好说，如果有，呵呵！回家绝对又是一场腥风血雨。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">记得我有一次去医院产检，是老公陪着我去的，刚进门坐下没多久，那位看起来有些年纪的女医生扭头就说，孕妇留下就可以了，麻烦家属先出去。她是成年人了，在这里我可以保证她的安全，请在门口耐心等待！然后老公说了句我在外面等你！就出去了。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/18aa0005cd21a5bfb792\" img_width=\"320\" img_height=\"240\" alt=\"产检被医生1个问题羞红了老脸，真好尴尬啊\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">常规检查：测量血压和体重，是每次产前检查的保留项目;而首次检查这两项的结果，有助于医生掌握准妈妈的健康状况，如果以后某次检查孕妇的血压或体重上升得比较快，医生就会有所警觉，并采取相应的方法解决。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">当时不知道是怎么回事，默默地等着医生问话，医生一开口就把我吓着了，她很和蔼地说：小姑娘，以前有过流产史吗？我愣了一下，感觉有些难以启口。这时候只有我们两个在一个房间，我也只能硬着头皮说了。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">“以前流过一个。”我说。医生倒没什么异样的表情，还是很和蔼地继续问了一下话，检查了一下身体，记了些东西就让我出去了。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/18ab0005523cec7c24a7\" img_width=\"461\" img_height=\"306\" alt=\"产检被医生1个问题羞红了老脸，真好尴尬啊\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">后来我才知道医生问有没有流过是为了了解我的身体状况。听在妇产科工作的同学说，有流产史的孕妇，因为之前的子宫内壁有损伤，孕期更容易发生胎盘粘连，产后大出血的风险也会比常人高。如果有过胎停育、畸形胎儿或是自然流产的，医生就会特别注意这胎。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">妊娠不足28周、胎儿体重不足1000g而终止妊娠者称流产。流产发生于妊娠12周前者称早期流产，发生在妊娠12周至不足28周者称晚期流产。流产又分为自然流产和人工流产，自然流产的发病率占全部妊娠的15%左右，多数为早期流产。还有两种特别流产类型：稽留流产和习惯性流产。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">通常在孕期，妇科医生是没有办法看出孕妇有没有流过产的，只有通过检查才能知道。但为了胎儿健康着想，还是建议孕妈不要隐瞒，如实向医生交待。尤其是之前有过自然流产或习惯性流产的孕妈。如果怕家人在不方便回答，就先说没有。等生孩子的时候再告诉医生以前流过，如果遇到什么突发状况医生也能及时补救。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/18aa0005cd20d5871242\" img_width=\"384\" img_height=\"240\" alt=\"产检被医生1个问题羞红了老脸，真好尴尬啊\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">早期自然流产时，染色体异常的胚胎占50%～60%，多为染色体数目异常，其次为染色体结构异常。数目异常有三体、三倍体及X单体等；结构异常有染色体断裂、倒置、缺失和易位。染色体异常的胚胎多数结局为流产，极少数可能继续发育成胎儿，但出生后也会发生某些功能异常或合并畸形。若已流产，妊娠产物有时仅为一空孕囊或已退化的胚胎。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">不过好像并不是所有孕妈都像我能遇到这么负责的医生，之前听一个孕妈说以前自己年轻不懂事，有了孩子又没有能力养，又害怕别人说闲话就把孩子给打掉了，后来又怀了一胎，但是当时男朋友却和另外一个女的好上了，最后也只能打掉了。后来她遇到了现在的老公，对她还不错，但是婆婆有点不喜欢她，也不知道为什么。后来婆婆和她一起去产检，好死不死地医生也不避讳地问了这个问题，那时候尴尬地只想找洞钻了。最后她如实告诉医生流过两个。婆婆听到后对她的态度更是一落千丈，在家老给她脸色看，也是可怜。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ee10003fa52fe4c8f15\" img_width=\"1160\" img_height=\"1280\" alt=\"产检被医生1个问题羞红了老脸，真好尴尬啊\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">流产过的也不用担心，现在很多医生在产检时都会先让家人在外面等候，即便有过流产经历回答也不会太尴尬。</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">妊娠早期时，除卵巢的妊娠黄体分泌孕激素外，胎盘滋养细胞亦逐渐产生孕激素。妊娠8周后，胎盘逐渐成为产生孕激素的主要场所。除孕激素外，胎盘还合成其他激素如β-绒毛膜促性腺激素、胎盘生乳素及雌激素等。早孕时，上述激素值下降，妊娠难以继续而致流产。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">妊娠犹如同种异体移植，胚胎与母体间存在复杂而特殊的免疫学关系，这种关系使胚胎不被排斥。若母儿双方免疫不适应，则可引起母体对胚胎的排斥而致流产。有关免疫因素主要有父方的组织相容性抗原、胎儿特异抗原、血型抗原、母体细胞免疫调节失调等。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">流产的主要症状是阴道流血和腹痛。阴道流血发生在妊娠12周以内流产者，开始时绒毛与蜕膜分离，血窦开放，即开始出血。当胚胎完全分离排出后，由于子宫收缩，出血停止。早期流产的全过程均伴有阴道流血；晚期流产时，胎盘已形成，流产过程与早产相似，胎盘继胎儿娩出后排出，一般出血不多，特点是往往先有腹痛，然后出现阴道流血。流产时腹痛系阵发性宫缩样疼痛，早期流产出现阴道流血后，胚胎分离及宫腔内存有的血块刺激子宫收缩，出现阵发性下腹疼痛，特点是阴道流血往往出现在腹痛之前。晚期流产则先有阵发性子宫收缩，然后胎盘剥离，故阴道流血出现在腹痛之后。流产时检查子宫大小、宫颈口是否扩张以及是否破膜，根据妊娠周数及流产过程不同而异。</p><p><br/></p>', '1', '1', '0', '健康网', '凌寒', '', '1500619321', '1500619321', '2', '', '');
INSERT INTO `news_article` VALUES ('22', '涨知识：西瓜这样挑，才能知道它熟不熟、好不好吃！', '5', '20170721\\de9341b1e1e6353410f41746b1a7c856.jpg', '要说有什么水果能够代表夏天的，那一定是西瓜君莫属啦，甜甜的冰镇西瓜，一口下去就把所有燥热驱散了……  然而，想法很完美，现实却很无奈...因为买西瓜的时候，很多人不知道怎么判断它熟不熟、甜不甜，结果买回来的时候又生又淡，那就很尴尬了。', '西瓜，熟不熟', '<h1 style=\"margin: 36px 0px 22px; padding: 0px; font-size: 24px; line-height: 32px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">西瓜君挑选日记</h1><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">要说有什么水果能够代表夏天的，那一定是西瓜君莫属啦，甜甜的冰镇西瓜，一口下去就把所有燥热驱散了……</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">然而，想法很完美，现实却很无奈...因为买西瓜的时候，很多人不知道怎么判断它熟不熟、甜不甜，结果买回来的时候又生又淡，那就很尴尬了。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">氮素，别担心！有小鲜在，怎么可能让大家发生这种事呢，接下来我就来跟大家说说，如何判断哪些西瓜更熟更好吃！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ed20004ce510925681b\" img_width=\"450\" img_height=\"325\" alt=\"涨知识：西瓜这样挑，才能知道它熟不熟、好不好吃！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><h1 style=\"margin: 36px 0px 22px; padding: 0px; font-size: 24px; line-height: 32px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">开讲</h1><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">01</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">买西瓜的时候，用手掌轻拍或用手指弹西瓜，熟瓜会发出“嘭嘭”的声音，生瓜会发出“当当”声音，而过熟的瓜则是“噗噗”声。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2edd0004dbc77ea29f49\" img_width=\"600\" img_height=\"398\" alt=\"涨知识：西瓜这样挑，才能知道它熟不熟、好不好吃！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">02</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">同样大小的两个西瓜，熟的西瓜比较轻而生的比较重，熟瓜放入水中会浮起来，生瓜放到水中会沉下去。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2edd0004e9d656868545\" img_width=\"750\" img_height=\"562\" alt=\"涨知识：西瓜这样挑，才能知道它熟不熟、好不好吃！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">03</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">瓜皮颜色淡、青，还有细白毛的，一般来说还是比较生的瓜；成熟的西瓜瓜皮比较光滑、发亮。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ecf0004b364ad9f96a9\" img_width=\"782\" img_height=\"401\" alt=\"涨知识：西瓜这样挑，才能知道它熟不熟、好不好吃！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">04</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">如果有小伙伴仔细看过西瓜，就发现西瓜的纹路是不一样的。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">纹路明显、表皮光滑、体型匀称的，</span>西瓜一般比较好吃，而尖头歪脑、表皮黏黏软软的，肯定不好吃。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">06</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">还有一点估计平时大家都不太会注意到，西瓜的柄是有弯曲和笔直的之分的，柄较弯曲的西瓜会比较甜，柄较直的西瓜没这么好吃。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ed500040eb65f6ccc57\" img_width=\"630\" img_height=\"396\" alt=\"涨知识：西瓜这样挑，才能知道它熟不熟、好不好吃！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">06</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">西瓜在生长过程中，一部分压在地面上形成了一个白圈，如果西瓜不是自然成熟的，那个白点不会那么明显，而好的西瓜是非常明显的。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">07</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">最后，西瓜底部的有个小圆圈，圆圈越小越好，底部的圆圈越大西瓜的皮就越厚，瓜就没这么好吃啦。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2ed600028984bc546b22\" img_width=\"800\" img_height=\"506\" alt=\"涨知识：西瓜这样挑，才能知道它熟不熟、好不好吃！\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">好啦，就素酱紫，根据以上技巧，相信大家都能够买到好吃的西瓜啦。</p><p><br/></p>', '1', '1', '0', '健康网', '凌寒', '', '1500619502', '1500619502', '2', '', '');
INSERT INTO `news_article` VALUES ('23', '女人为什么离不开男人？夫妻间谁更需要谁？', '4', '20170721\\40c7bf8eef0e9be4860009d1410a2b72.jpg', '1、小伙子心仪一姑娘很久，于是借机找她帮了个忙。事成之后，小伙子说：“我该怎么感谢你呢，我娶你吧！”姑娘仔细看了看他的模样说：“你怎么能恩将仇报呢？”  2、一女同事忧心忡忡的问我：我家的牛每次才耕一会田就没力气了，怎么办？', '夫妻，女人，男人', '<p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">开心一刻：</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">1、小伙子心仪一姑娘很久，于是借机找她帮了个忙。事成之后，小伙子说：“我该怎么感谢你呢，我娶你吧！”姑娘仔细看了看他的模样说：“你怎么能恩将仇报呢？”</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2、一女同事忧心忡忡的问我：我家的牛每次才耕一会田就没力气了，怎么办？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">我不假思索的回答道：卖牛肉吧！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">结果女同事长叹一声：哎。。。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">总觉得哪里不对，她家住21楼，怎么养牛？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p9.pstatp.com/large/2ee4000330c61fc2391a\" img_width=\"500\" img_height=\"386\" alt=\"女人为什么离不开男人？夫妻间谁更需要谁？\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">3、丈夫说：有个一举四得的事情，咱做不做？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">妻子说：做呀。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">丈夫说：咱请个保姆吧，我走了，她陪你，你走了，她陪我，咱们都走了，她为咱收拾家，咱们都在家，她为我们洗衣、做饭。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2、儿子午睡起床，老婆喂水，儿子不肯喝，拉扯了半天还是不喝，老婆怒了，“你又不是三岁小孩，拽什么啊，不喝拉倒。”然后就撇下我们父子俩走了。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">我无辜地看着儿子，儿子一脸无邪地望着我，宝贝啊，你妈没说错，你不是三岁小孩，你才一岁半……</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">男追女，隔座山，女追男，隔成纱！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">为啥女人离不开男人？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">对于已婚女士，丈夫对她最好的宠爱就是亲密的关系，执子之手与子偕老！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">无论是成婚一年，还是十年二十年，每当他牵起你的手，他都依然冲动的像个少年——这是女人的梦想。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">然而，现实往往有些残酷，婚姻的七年之痒让很多男人违背了当初的承诺！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ee60001b1d447bd1849\" img_width=\"640\" img_height=\"446\" alt=\"女人为什么离不开男人？夫妻间谁更需要谁？\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">其实，女人离不开男人，不仅仅是因为爱，最近，美国的科学家给出了以生理学为基础的研究成果，证明女人离不开男人，因为经常有男人宠爱的女人，对女性的身体有很多的好处：</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">1、有助减肥</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">据调查显示，一个热烈的接吻燃烧十二卡路里，30分钟的亲密.运动可以燃烧200卡路里。如果在过程中比较兴奋和热情，消耗的能量更多。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2、皮肤红润</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">人面桃花，说的就是新婚燕尔的新娘子。刺激和运动可以促进肾上腺素的产生，让人看起来气色更好，这可是任何化妆品都比不了的。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">反之，如果长时间不和伴侣进行运动，则皮肤血液循环不良，会导致粉刺、暗疮等皮肤病。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2ee200032f4b324e999a\" img_width=\"498\" img_height=\"370\" alt=\"女人为什么离不开男人？夫妻间谁更需要谁？\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">3、助眠</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">如果有一场酣畅淋漓的运动，可以释放促进睡眠的内啡肽，让夫妻迅速进入甜美的梦乡。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p9.pstatp.com/large/2ee50001b824fe1ae977\" img_width=\"620\" img_height=\"402\" alt=\"女人为什么离不开男人？夫妻间谁更需要谁？\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">夫妻之间的亲密关系，你属于哪种？一周一次，一周三次or一周五次或更多？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">我相信如果一周连一次都没有，女方也会崩溃的。如果男人总以太忙了，太累了为借口，女方最先怀疑的，就是你的身体或者是心里有了别人。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p9.pstatp.com/large/2ee10003e210a9d6db4c\" img_width=\"640\" img_height=\"427\" alt=\"女人为什么离不开男人？夫妻间谁更需要谁？\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">那么，如果频率不达标，吃什么有助于提高男人的体力呢？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">1、多不饱和脂肪酸——欧米伽3</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">医学研究表明，人类普遍缺乏欧米伽3，这是导致人体脂肪酸摄入不均衡的重要原因，也是引起肥胖、免疫力低下，高血脂，高血压等慢性疾病的重要因素，因此，补充欧米伽3亚麻酸，有助于增强免疫力，降低血脂，预防心脑血管疾病。欧米伽3是人体必需脂肪酸，存在于深海鱼和少数特殊的食用油中，例如苏麻油中欧米伽3亚麻酸含量高达65%，是世界上目前所知欧米伽3含量最高的食物。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">食用方法：苏麻油拌酸奶，把苏麻油5毫升加到100-200毫升酸奶里搅拌均匀食用即可，或者每天口服5毫升，或加到做好的菜里食用。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2ee10003e2131c2b0ff5\" img_width=\"2309\" img_height=\"1731\" alt=\"女人为什么离不开男人？夫妻间谁更需要谁？\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2、丹芝粉</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">丹芝是灵芝中的上品，对增强人体免疫力有很好的作用。失眠多梦，免疫力低下，容易感冒，脂肪肝，这些是中年男性常见问题，遇到这些问题，可以食用丹芝粉，丹芝是灵芝的一种，色泽棕红色，被称为丹芝，是灵芝中药效最好的品种，含有多糖和三萜类成分，有很好的安神和增强免疫力，抗肿瘤的作用。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ee4000330c7ae0dd6dd\" img_width=\"588\" img_height=\"436\" alt=\"女人为什么离不开男人？夫妻间谁更需要谁？\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">3.蒲公英包谷须茶</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">配方：蒲公英2g、包谷须1g、玫瑰0.5g、玉竹0.5g</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">糖尿病是男性最常见的疾病，一般伴有肥胖等，严重的糖尿病也是导致男性阳痿的一个重要原因。包谷须也叫玉米须，中医认为玉米须含有多糖和黄酮类成分，对降低血糖有帮助，而蒲公英则能去火消炎，利尿，排毒，用蒲公英和包谷须配制成茶，有降低血糖，预防糖尿病的作用。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ee50001b82587f1c852\" img_width=\"640\" img_height=\"360\" alt=\"女人为什么离不开男人？夫妻间谁更需要谁？\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">4、南瓜子油</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">男性最常见的生殖系统问题就是前列腺增生，前列腺增生容易导致尿频尿急，排尿困难，尿不尽等，十分影响男性的形象，同时严重的也会导致射精困难，因此有前列腺增生或者尿频尿急的男性，可以服用南瓜子油。欧洲及美国的研究表明，南瓜子油含有一种特殊的植物甾醇类物质，类似男性荷尔蒙，可以抑制前列腺的初期肿胀，对男性尿频尿急，排尿困难效果很好。每天5毫升直接口服，或者淋到做好的菜里食用，也可以用南瓜子油拌酸奶，据说能让男人更强大。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ee200032f4a2bc8619b\" img_width=\"640\" img_height=\"401\" alt=\"女人为什么离不开男人？夫妻间谁更需要谁？\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">当然，除了食补调理外，男人平常多做体育运动，练习俯卧撑等也有助于提高体能，延长时间。</p><p><br/></p>', '1', '1', '1', '健康网', '凌寒', '', '1500619645', '1500619645', '2', '', '');
INSERT INTO `news_article` VALUES ('24', '三伏天，40天的治病黄金时段，每天坚持喝它一碗.', '3', '20170721\\91a186153edec013ef39106ee185af0a.jpg', '大家都估计听说过“冬病夏治”，恰好的40天治病黄金时段，就在这个夏天。现在已经入伏好几天，湿气也开始嚣张起来了。小编看了一下身边的人，流鼻涕的，打喷嚏的，戴口罩的。看来难道夏天跟流感更配吗？然而传统医学认为，夏秋之交热而多雨，湿气较重。湿气重就容易生病。', '三伏天，治病，湿气', '<p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">大家都估计听说过“冬病夏治”，恰好的40天治病黄金时段，就在这个夏天。现在已经入伏好几天，湿气也开始嚣张起来了。小编看了一下身边的人，流鼻涕的，打喷嚏的，戴口罩的。看来难道夏天跟流感更配吗？然而传统医学认为，夏秋之交热而多雨，湿气较重。湿气重就容易生病。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2ee200029dff36ecf21b\" img_width=\"997\" img_height=\"577\" alt=\"三伏天，40天的治病黄金时段，每天坚持喝它一碗，半个月湿气全无\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">三伏天，不知道是福还是祸，虽然身体很容易就被侵袭，但三伏天却是“冬病夏治”的黄金时段。三伏时节，不仅是人体阳气最足的时节，同时，在三伏时节。人体的毛孔是处于张开的状态，血液循环也较其他时节快，新陈代谢也加快了。这时候，不论是针灸、拔罐、敷贴驱寒，都会起到事半功倍的效果。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">所以中医专家们提出“三伏补阳祛湿治病，三九滋阴护阳防病“。那我们就来聊聊，三伏天应该吃什么？</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2ee20002b10535e5c4a4\" img_width=\"480\" img_height=\"278\" alt=\"三伏天，40天的治病黄金时段，每天坚持喝它一碗，半个月湿气全无\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">一、吃清热解暑、健脾利湿食物</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">暑天湿气重，我们应该多吃一些凉性，苦味的食物，有助于体内的湿气毒素排出来。例如：苦瓜，薏米，而薏米又名薏苡仁，是常用的中药。又是普遍、常吃的食物，具有味甘淡微寒,有利水消肿、健脾去湿、舒筋除痹、清热排脓等功效。我们可以用它来煮粥，又或者泡水喝！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">薏米枸杞茶</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">做法：取若干颗红枣去核放入茶壶中，再加入适量枸杞和冰糖，放入炒好的薏米，倒入开水，浸泡十分钟左右，每天坚持喝一杯，就能达到利水消肿，健脾祛湿的效果。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">薏米红豆粥</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">材料：红豆25克、薏米30克、枸杞18克、芡实 20克</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">做法：一把红豆，一把薏米洗净，睡觉前浸泡上，早上起来用电高压锅煮大概15分钟就好，时间到锅会自动停止，简单的薏米红豆去湿粥就做好了。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ee100035f8ae01d93e3\" img_width=\"292\" img_height=\"324\" alt=\"三伏天，40天的治病黄金时段，每天坚持喝它一碗，半个月湿气全无\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">二、吃益气养阴，补血润肺的食物</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">大暑天气酷热，出汗较多，容易耗气伤阴，应常吃一些益气养阴且清淡的食物以增强体质。如山药、大枣、海参、鸡蛋、牛奶、蜂蜜、莲藕、木耳、豆浆、百合粥、菊花粥等。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p9.pstatp.com/large/2ee20002b3ac71907654\" img_width=\"413\" img_height=\"451\" alt=\"三伏天，40天的治病黄金时段，每天坚持喝它一碗，半个月湿气全无\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">三、多吃新鲜的蔬果，少吃凉菜或油炸性食物</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">夏天水果缤纷上市，但水果可不能乱吃，不能过量地吃一些当季的水果，因为夏天的水果多半寒湿，所以注意要吃一些健康的新鲜的水果，例如：苹果，香蕉，雪梨，葡萄等等。晚上的凉菜也不能吃，因为凉菜放在冰箱是会滋生细菌的。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700;\">三伏天，40天的治病黄金时段</span></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">这时我们就要为冬天的病做好预防准备了</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">1、适当的运动，早上不宜太早跑步，因为夏天的清晨，雾气湿重，很容易导致人着凉的。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2、泡茶养生，翠钱茶是一种养生祛湿消水肿的茶，其实翠钱就是我们日常生活中的桑叶，桑叶具有消肿降血脂的功效，在夏天，我们可以去药店买一些翠钱泡茶喝，有助于养生祛湿，保健康。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">3、晚上的时候，我们可以用姜水泡一下脚，脚底有许多我们不知道的穴位，用姜水，泡一泡，简单易做，就可以消除疲劳，还有助于睡眠。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">总结：一年之中只有那么40天的治病黄金时段，错过了，就要等下一年噢，赶紧祛湿养生吧！</p><p><br/></p>', '1', '1', '1', '健康网', '凌寒', '', '1500621948', '1500621948', '2', '', '');
INSERT INTO `news_article` VALUES ('25', '怎样炒青菜好吃又不黄？', '2', '20170721\\4027cd2cb5a9bd4649c6ab02066acc29.jpg', '炒青菜是我们最常吃的家常菜，但是很多时候我们会发现，炒出的青菜很容易颜色变黄，影响食欲。那么怎样炒青菜不变黄呢？其实，要想炒出又绿又嫩的青菜，需要掌握以下几个小技巧。', '青菜，健康', '<p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">炒青菜是我们最常吃的家常菜，但是很多时候我们会发现，炒出的青菜很容易颜色变黄，影响食欲。那么怎样炒青菜不变黄呢？其实，要想炒出又绿又嫩的青菜，需要掌握以下几个小技巧。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　炒青菜好吃的技巧：</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　第一、炒青菜的油量，稍微比平常烹饪多些，没有油份滋润的青菜，也像我们没有各种护肤品滋润的皮肤，没有水盈盈的感觉。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　第二、炒青菜的火候，基本以旺火快炒为主，千万不要用中小火慢慢煮，记得是快炒。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　第三、炒菜时，放盐最关键！有些青菜，比如生菜、大白菜，本身水分已经很足，就一定要在炒到八成熟的时候再放盐，如果早放盐，就会出一滩水，也容易影响菜的颜色；有些青菜，比如通心菜、红薯叶、南瓜苗之类，水分并不多，最好在放油之后，把需要的盐份倒进油里融化掉，再倒青菜入锅内旺火快炒，这样在炒的过程中，会逼出青菜中的一小部分水分，更好地保护此类青菜的翠色。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　第四、有些青菜，在入盘之后千万不要堆起来，要用筷子在盘子中央挑出一处空地，让青菜更快散热，才不会因温度过高使青菜变色。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　炒青菜不变黄的技巧大致就这么多了，希望大家实践的时候再细心观察，总结出更多的技巧。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">第五、还可以青菜的叶和梗分开炒。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p9.pstatp.com/large/2c6100109a6f0c22e075\" img_width=\"552\" img_height=\"352\" style=\"border: 0px; display: block; max-width: 100%; margin-bottom: 10px; margin-top: 10px;\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">1、把青菜洗好；</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2c5f0010a863747be66e\" img_width=\"552\" img_height=\"352\" style=\"border: 0px; display: block; max-width: 100%; margin-bottom: 10px; margin-top: 10px;\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2、把菜籽油熟后，先下菜梗炒；</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2c6f00151b28feef5b9a\" img_width=\"552\" img_height=\"352\" style=\"border: 0px; display: block; max-width: 100%; margin-bottom: 10px; margin-top: 10px;\"/>3、再加菜叶炒；<img src=\"http://p1.pstatp.com/large/2c5f0010a86861ca4e36\" img_width=\"552\" img_height=\"352\" style=\"border: 0px; display: block; max-width: 100%; margin-bottom: 10px; margin-top: 10px;\"/>4、炒软后加盐、糖再炒；</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　酒店炒的青菜，大部分先焯过水，然后再用猛火翻炒入味，所以色泽很漂亮，但是这样操作，极大地造成了青菜中的VC流失，而且在家庭操作中，火候达不到酒店的要求，即使如此操作也不尽人意。不过，只要我们掌握了以上的四大技巧，用家里的火量也能炒出翠绿又脆嫩的一碟加分青菜。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2c6e001096aa2809773d\" img_width=\"552\" img_height=\"352\" style=\"border: 0px; display: block; max-width: 100%; margin-bottom: 10px; margin-top: 10px;\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　温馨提示：</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　1、青菜不要炒得太熟，否则营养会流失。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　2、大火炒会比较香。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　食疗作用：</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　1．提供营养，强身健体青菜为含维生素和矿物质最丰富的蔬菜之一，一个成年人如果每天吃500克青菜，就能满足人体所需的维生素、胡萝卜素、钙、铁等，为保证身体的生理需要提供物质条件，有助于增强机体免疫能力。　　</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　2．保持血管弹性青菜中含有大量粗纤维，其进人人体内与脂肪结合后，可防止血浆胆固醇形成，促使胆固醇代谢物——胆酸得以排出体外，以减少动脉粥样硬化的形成，从而保持血管弹性。　　</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　3．润泽皮肤，延缓衰老青菜中含有大量胡萝卜素（比豆类多1倍，比西红柿、瓜类多4倍）和维生素C，进入人体后，可促进皮肤细胞代谢，防止皮肤粗糙及色素沉着，使皮肤亮洁。延缓衰老。　　</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　4．防癌抗癌青菜中所含的维生素C，在体内会形成一种“透明质酸抑制物”，这种物质具有抗癌作用，可使癌细胞丧失活力。此外，青菜中含有的粗纤维可促进大肠蠕动，增加大肠内毒素的排出，达到防癌抗癌的目的。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　美食禁忌：</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; line-height: 28px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　青菜性偏寒，凡脾胃虚寒、大便溏泄者不宜多食。</p><p><br/></p>', '1', '1', '0', '健康网', '凌寒', '', '1500624371', '1500624371', '2', '', '');
INSERT INTO `news_article` VALUES ('26', '外国人感叹：中国吃货改变全球产业，我们不敢得罪他们', '1', '20170721\\e6fb418aaccb8b2134e0ce61cf746f55.jpg', '吃也能吃出名堂？你想不到中国14亿人的胃口竟然能改变全球的种植业和粮食销售方式。一些国家可羡慕中国有这么多吃货，在2016年中国人仅仅就吃就吃出了一个瑞士一年的GDP！好吓人的数据。', '吃货，美食', '<p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">吃也能吃出名堂？你想不到中国14亿人的胃口竟然能改变全球的种植业和粮食销售方式。一些国家可羡慕中国有这么多吃货，在2016年中国人仅仅就吃就吃出了一个瑞士一年的GDP！好吓人的数据。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ed800002c14275bc09e\" img_width=\"688\" img_height=\"516\" alt=\"外国人感叹：中国吃货改变全球产业，我们不敢得罪他们\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">上图为中国吃货爆满的夜市</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">外国人完全没想到中国这一帮吃货还开创出了一个巨大的商机，为迎合中国人的胃口，一些国家开始绞尽脑汁想办法。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">比如原先瞧不起中国消费市场的智利，在几年前智利的车厘子只有很少一部分销售到中国，但是现在挤破脑袋也要打开中国吃货的大门，智利总统还特意在中国葡萄酒推销会上为他们自己的葡萄酒站台。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p1.pstatp.com/large/2ed70003d850d676e42a\" img_width=\"696\" img_height=\"463\" alt=\"外国人感叹：中国吃货改变全球产业，我们不敢得罪他们\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">上图为智利车厘子出口包装流水线</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">再说德国泛滥的大闸蟹，这是中国吃货最早伤透脑筋的东西。由于德国人本来是不吃大闸蟹，但是听到中国人吃大闸蟹于是就发现了商机，一些渔业公司甚至会选择不远千里将大闸蟹运送到中国等亚洲超市饭店，每公斤还可以买到7-8欧元利润丰厚，现在德国人正在想办法大批量出口到中国来，有求于人啊！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ed70003d84fb5a4719b\" img_width=\"637\" img_height=\"397\" alt=\"外国人感叹：中国吃货改变全球产业，我们不敢得罪他们\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">上图为一个德国人在养殖大闸蟹</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">澳大利亚本来是以采矿业文明于世，但是由于矿产资源属于不可再生资源，很快就枯竭了。可澳大利亚还有一个得天独厚的地理条件适合牛羊的养殖，恰好发现中国大陆这一个庞大到吓人的消费市场，赶紧养牛羊向中国出口吧！目前牛肉出口几乎已经洽谈中，商人表示不敢得罪中国人啊！</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ed800002c15e65f1c7b\" img_width=\"690\" img_height=\"459\" alt=\"外国人感叹：中国吃货改变全球产业，我们不敢得罪他们\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">上图为澳大利亚养牛场</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">为了满足中国人的胃口，加拿大人甚至为中国人养起了鸭子！这件事要从1912年说起，那时候的加拿大人是不养鸭子的，但是发现中国这个市场之后，一些加拿大人开始养起了鸭子，一部分出口到中国，另一部分效仿中国餐饮模式，卖给在加拿大拥有庞大数量的华人。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ee200002c20afe5f570\" img_width=\"640\" img_height=\"449\" alt=\"外国人感叹：中国吃货改变全球产业，我们不敢得罪他们\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">上图为加拿大养殖的北京填鸭</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">在西班牙甚至有一个口号“为华人种菜”这又是从何说起？不得不说中国人在世界分布太广了。西班牙一个叫埃尔佩雷约的小镇靠着温润气候种植中国人喜欢吃的蔬菜大获成功，目前在该地拥有为欧洲多国提供近20种中国蔬菜的基地，生菜、西葫芦、西兰花等是销售最好的中国蔬菜。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p3.pstatp.com/large/2ee10000db7bbeb57236\" img_width=\"580\" img_height=\"300\" alt=\"外国人感叹：中国吃货改变全球产业，我们不敢得罪他们\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">上图为西班牙妇女采摘种植的小青菜</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">再说说美国，这个世界大国，现在也不得不重视中国吃货的胃口了，目前中国已经超越加拿大成为美国农产品最大出口市场。举个例子在加州因为水荒，水价大涨，但是依然会选择种耗水量高但是中国人非常喜欢的柑橘，因为对加州的这些农场主来说中国人对他们太重要了，直接影响到农场的生死。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"http://p9.pstatp.com/large/2edb00002c14c42b760e\" img_width=\"750\" img_height=\"518\" alt=\"外国人感叹：中国吃货改变全球产业，我们不敢得罪他们\" inline=\"0\" style=\"border: 0px; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">上图为美国柑橘种植园</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">在世界经济持续低迷的情况下只有中国一枝独秀，为各国提供了开阔的市场，大把的资金，也难怪他们不敢得罪中国人了。</p><p><br/></p>', '1', '1', '0', '健康网', '凌寒', '', '1500624565', '1500624565', '2', '', '');

-- ----------------------------
-- Table structure for news_article_cate
-- ----------------------------
DROP TABLE IF EXISTS `news_article_cate`;
CREATE TABLE `news_article_cate` (
  `id` smallint(5) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(32) DEFAULT NULL COMMENT '分类名称',
  `orderby` varchar(10) DEFAULT '100' COMMENT '排序',
  `create_time` int(11) DEFAULT NULL COMMENT '创建时间',
  `update_time` int(11) DEFAULT NULL COMMENT '更新时间',
  `status` tinyint(1) DEFAULT NULL,
  `pid` smallint(5) DEFAULT NULL COMMENT '上级分类',
  `path` varchar(32) DEFAULT NULL COMMENT '分类路径',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=0 DEFAULT CHARSET=utf8 COMMENT='文章分类';

