DROP TABLE IF EXISTS `news_article_error`;
CREATE TABLE `news_article_error` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `content` varchar(1000) DEFAULT '' COMMENT '错误内容',
  `advice` varchar(1000) DEFAULT '' COMMENT '建议',
  `type` int(11) NOT NULL DEFAULT '0' COMMENT '错误类型',
  `create_time` int(11) NOT NULL DEFAULT '0' COMMENT '创建时间',
  `update_time` int(11) NOT NULL DEFAULT '0' COMMENT '更新时间',
  `article_id` int(11) NOT NULL DEFAULT '0' COMMENT '纠错对象',
  PRIMARY KEY (`id`)
