-- ----------------------------
-- Table structure for news_article_report
-- ----------------------------
DROP TABLE IF EXISTS `news_article_report`;
CREATE TABLE `news_article_report` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '举报ID',
  `uid` int(11) NOT NULL DEFAULT '0' COMMENT '用户ID',
  `content` varchar(1000) DEFAULT '' COMMENT '举报内容',
  `status` int(11) NOT NULL DEFAULT '0' COMMENT '评论状态',
  `ip` char(60) NOT NULL DEFAULT '' COMMENT '举报者IP',
  `create_time` int(11) NOT NULL DEFAULT '0' COMMENT '创建时间',
  `update_time` int(11) NOT NULL DEFAULT '0' COMMENT '更新时间',
  `article_id` int(11) NOT NULL DEFAULT '0' COMMENT '举报对象',
  `report` tinyint(4) NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `userarticle` (`uid`,`article_id`) USING BTREE
) ENGINE=MyISAM AUTO_INCREMENT=0 DEFAULT CHARSET=utf8 COMMENT='举报文章';

