-- ----------------------------
-- Table structure for news_collection
-- ----------------------------
DROP TABLE IF EXISTS `news_collection`;
CREATE TABLE `news_collection` (
  `id` tinyint(8) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `user_id` tinyint(5) NOT NULL DEFAULT '0' COMMENT '用户ID',
  `article_id` tinyint(5) NOT NULL DEFAULT '0' COMMENT '新闻ID',
  `create_time` int(11) DEFAULT NULL COMMENT '创建时间',
  `update_time` int(11) DEFAULT NULL COMMENT '修改时间',
  `status` enum('0','1') NOT NULL DEFAULT '1' COMMENT '0：取消收藏 1：已收藏',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=0 DEFAULT CHARSET=utf8 COMMENT='收藏表';

-- ----------------------------
-- Records of news_collection
-- ----------------------------

-- ----------------------------
-- Table structure for news_comment
-- ----------------------------
DROP TABLE IF EXISTS `news_comment`;
CREATE TABLE `news_comment` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '评论ID',
  `uid` int(11) NOT NULL DEFAULT '0' COMMENT '用户ID',
  `content` varchar(1000) NOT NULL DEFAULT '' COMMENT '评论内容',
  `status` int(11) NOT NULL DEFAULT '0' COMMENT '评论状态',
  `ip` bigint(20) NOT NULL DEFAULT '0' COMMENT '评论者IP',
  `create_time` int(11) NOT NULL DEFAULT '0' COMMENT '创建时间',
  `update_time` int(11) NOT NULL DEFAULT '0' COMMENT '更新时间',
  `article_id` int(11) NOT NULL DEFAULT '0' COMMENT '评论对象',
  `zan` int(11) DEFAULT '0' COMMENT '点赞数量',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=0 DEFAULT CHARSET=utf8 COMMENT='评论';

-- ----------------------------
-- Records of news_comment
-- ----------------------------

-- ----------------------------
-- Table structure for news_log
-- ----------------------------
DROP TABLE IF EXISTS `news_log`;
CREATE TABLE `news_log` (
  `log_id` int(11) NOT NULL AUTO_INCREMENT,
  `admin_id` int(11) DEFAULT NULL COMMENT '用户ID',
  `admin_name` varchar(50) DEFAULT NULL COMMENT '用户姓名',
  `description` varchar(300) DEFAULT NULL COMMENT '描述',
  `ip` char(60) DEFAULT NULL COMMENT 'IP地址',
  `status` tinyint(1) DEFAULT NULL COMMENT '1 成功 2 失败',
  `add_time` int(11) DEFAULT NULL COMMENT '添加时间',
  PRIMARY KEY (`log_id`)
) ENGINE=MyISAM AUTO_INCREMENT=0 DEFAULT CHARSET=utf8 COMMENT='操作记录';

-- ----------------------------
-- Records of news_log
-- ----------------------------

-- ----------------------------
-- Table structure for news_member
-- ----------------------------
DROP TABLE IF EXISTS `news_member`;
CREATE TABLE `news_member` (
  `member_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `account` varchar(64) DEFAULT NULL COMMENT '邮件或者手机',
  `password` char(32) DEFAULT NULL,
  `face` varchar(128) DEFAULT NULL COMMENT '头像',
  `nickname` varchar(32) DEFAULT NULL COMMENT '昵称',
  `integral` int(11) DEFAULT '0' COMMENT '积分',
  `money` int(11) DEFAULT '0' COMMENT '账户余额',
  `reg_time` int(11) DEFAULT '0' COMMENT '注册时间',
  `reg_ip` varchar(15) DEFAULT NULL COMMENT '注册IP',
  `last_time` int(11) DEFAULT NULL COMMENT '最后一次登录',
  `last_ip` varchar(15) DEFAULT NULL COMMENT '最后一次登录IP',
  `status` tinyint(1) DEFAULT NULL COMMENT '1激活  0 未激活',
  `closed` tinyint(1) DEFAULT '0',
  `mobile` varchar(11) DEFAULT NULL COMMENT '认证的手机号码',
  `token` char(32) DEFAULT '0' COMMENT '令牌',
  `session_id` varchar(20) DEFAULT NULL,
  `sex` int(10) DEFAULT NULL COMMENT '1男2女',
  `birthday` varchar(255) DEFAULT NULL,
  `open_id` varchar(50) DEFAULT NULL,
  PRIMARY KEY (`member_id`)
) ENGINE=MyISAM AUTO_INCREMENT=0 DEFAULT CHARSET=utf8 COMMENT='用户详情';

-- ----------------------------
-- Records of news_member
-- ----------------------------

-- ----------------------------
-- Table structure for news_report
-- ----------------------------
DROP TABLE IF EXISTS `news_report`;
CREATE TABLE `news_report` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '举报ID',
  `uid` int(11) NOT NULL DEFAULT '0' COMMENT '用户ID',
  `content` varchar(1000) DEFAULT '' COMMENT '举报内容',
  `status` int(11) NOT NULL DEFAULT '0' COMMENT '评论状态',
  `ip` char(60) NOT NULL DEFAULT '' COMMENT '举报者IP',
  `create_time` int(11) NOT NULL DEFAULT '0' COMMENT '创建时间',
  `update_time` int(11) NOT NULL DEFAULT '0' COMMENT '更新时间',
  `comment_id` int(11) NOT NULL DEFAULT '0' COMMENT '举报对象',
  `report` tinyint(4) NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `usercomment` (`uid`,`comment_id`) USING BTREE
) ENGINE=MyISAM AUTO_INCREMENT=0 DEFAULT CHARSET=utf8 COMMENT='举报评论';

-- ----------------------------
-- Records of news_report
-- ----------------------------

-- ----------------------------
-- Table structure for news_special
-- ----------------------------
DROP TABLE IF EXISTS `news_special`;
CREATE TABLE `news_special` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `name` varchar(64) NOT NULL DEFAULT '' COMMENT '专题别名',
  `title` varchar(128) NOT NULL COMMENT '标题',
  `photo` varchar(64) DEFAULT '' COMMENT '缩图',
  `remark` varchar(256) DEFAULT '' COMMENT '描述',
  `keyword` varchar(256) DEFAULT '' COMMENT '文章关键字',
  `views` int(11) NOT NULL DEFAULT '1' COMMENT '浏览量',
  `is_tui` int(1) DEFAULT '0' COMMENT '是否推荐',
  `writer` varchar(64) NOT NULL COMMENT '作者',
  `create_time` int(11) NOT NULL COMMENT '创建时间',
  `update_time` int(11) DEFAULT NULL COMMENT '更新时间',
  `sort` varchar(10) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `a_title` (`title`)
) ENGINE=MyISAM AUTO_INCREMENT=0 DEFAULT CHARSET=utf8 COMMENT='专题';

-- ----------------------------
-- Records of news_special
-- ----------------------------

-- ----------------------------
-- Table structure for news_special_article
-- ----------------------------
DROP TABLE IF EXISTS `news_special_article`;
CREATE TABLE `news_special_article` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `title` varchar(128) NOT NULL COMMENT '标题',
  `special_id` int(11) NOT NULL DEFAULT '1' COMMENT '专题外键',
  `photo` varchar(64) DEFAULT '' COMMENT '缩图',
  `remark` varchar(256) DEFAULT '' COMMENT '描述',
  `keyword` varchar(32) DEFAULT '' COMMENT '关键字',
  `content` text NOT NULL COMMENT '内容',
  `views` int(11) NOT NULL DEFAULT '1' COMMENT '浏览量',
  `type` varchar(128) NOT NULL DEFAULT '1' COMMENT '二次分类',
  `is_tui` int(1) DEFAULT '0' COMMENT '是否推荐',
  `from` varchar(16) NOT NULL DEFAULT '' COMMENT '来源',
  `writer` varchar(64) NOT NULL COMMENT '作者',
  `create_time` int(11) NOT NULL COMMENT '创建时间',
  `update_time` int(11) DEFAULT NULL COMMENT '更新时间',
  `status` enum('1','0') NOT NULL DEFAULT '1' COMMENT '0删除 1发布',
  `sort` varchar(10) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `a_title` (`title`)
) ENGINE=MyISAM AUTO_INCREMENT=0 DEFAULT CHARSET=utf8 COMMENT='专题文章';

-- ----------------------------
-- Records of news_special_article
-- ----------------------------
